@-moz-document domain("www.frandroid.com") {
/**
    Partout
*/

/* Titre. */
h2.has-text-gradient-blue-inverted {
	display: none;
}

/* Pub. */
div.is-margin-bottom-4, div.is-margin-bottom-6 {
	display: none;
}

/* Les "entre-article". */
div.has-background-gradient-blue {
	display: none;
}

div.sidebar {
	display: none;
}

div.article-content.with-sidebar {
	max-width: max-content;
}
}

@-moz-document url-prefix("https://www.frandroid.com/actualites") {
/**
    Juste sur la page actualité.
*/

/* Le bazar du haut. */
div.is-margin-bottom-3 {
	display: none;
}
}

@-moz-document regexp("https:\\/\\/www.frandroid.com\\/[^actualites].+") {
/**
    Toutes les pages sauf la page d'accueil et la page d'actualité.
*/

/* Demande d'email pour newsletter. */
div.newsletter-form.newsletter-form--columns {
    display: none;
}

/* Suggestions d'article. */
div.article-footer__related-posts {
    display: none;
}

div.breadcrumb ul {
	max-width: 100%;
}
}

@-moz-document domain("frandroid.com") {
/**
    Partout
*/


/* Fond dans le nouveau style. */
#page {
	background: radial-gradient(ellipse 100% 400% at 50% 100%, #23338a 0%, #832ca8 100%);
}

/* Header. */
div.header__top-bar {
	background-color: #191919;
}

/* Sous le header. */
div.header__container--categories-container {
	background-color: #232323;
}

/* Icones du sous-header. */
img.header__category-icon {
	filter: drop-shadow(0px 0px 7px #D3BFFF);
}

/* Texte sous les icones du sous-header. */
#header .header__category-link {
	color: #9393fd;
}

/* Fond article normal. */
li.post-card {
	background: #111;
}

/* Titre article normal. */
a.post-card__title-link {
    color: #fff;
}

a.rich-content__item-link {
    color: #fff !important;
}

/* Fond article spe. */
li.post-card--featured{
	background: linear-gradient(-25deg, #425fff 0%, #9030b9 100%);
}

}

@-moz-document url-prefix("https://www.frandroid.com/actualites") {
/**
    Juste sur la page actualité.
*/

/* Fond de titre. */
div.has-background-gradient-radial-blue-purple {
	background: initial;
}

div.is-padding-top-5 {
    padding-top: 0px;
}
}

@-moz-document regexp("https:\\/\\/www.frandroid.com\\/[^actualites].+") {
/**
    Toutes les pages sauf la page d'accueil et la page d'actualité.
*/

/* Sur-fond titre. */
div.is-padding-top-6 {
	padding-top: 1rem;
	background-color: #0000004a;
	padding: 5px;
	border-radius: 5px 5px 20px 20px;
}

/* Sur-fond contenu. */
div.article-content.with-sidebar {
	background-color: #0000004a;
	padding: 24px;
    padding-top: 5px;
    margin-top: 10px;
    border-radius: 20px;
}

/* Couleur du texte. */
body {
	color: #e0e0e0;
}

/* Fond commentaires. */
div.bg-content {
	background-color:#0000004a;
    border-radius: 20px;
    padding: 20px;
}

div.article-footer__comments-gradient {
	background: linear-gradient(to bottom, rgba(0,0,0,0) 0%, rgba(0, 0, 0, 0.7) 35%, black 85%, #000 100%);
}

/* Auteur de l'article. */
div.author-card {
	background: #0000004a;
}
}

@-moz-document url-prefix("https://disqus.com") {
/**
    La partie commentaire.
*/

/* Texte onglet commentaire. */
.tab-conversation.active > a {
	color: #d1d1d1 !important;
}

/* Texte commentaires. */
.post-content .post-message {
	color: #cfcece;
}

/* Textarea commentaire. */
.textarea-outer-wrapper .textarea-wrapper {
	background: #000;
	border: 2px solid #565656;
}

/* Les autres Textarea. */
.input--text {
	background: #000;
	border: 2px solid #565656;
}
}

