@-moz-document url-prefix("https://bmsemea.kaseya.com/") {
#intercom-container {
    display: none;
}

.TelerikModalOverlay {
    pointer-events: none;
}

.skin-blue .sidebar-menu > li > a {
    transition: background-color 0.2s ease;
}

.skin-blue .sidebar-menu > li.active > a {
    background-color: orange;
}

.skin-blue .sidebar-menu > li > a:hover {
    background-color: gray;
}

.tab-content,
.widget-notes,
.porlets-content {
    background-color: darkgray;
}

.news-widget-list {
    background-color: #fff;
    border-radius: 2px;
}

.paper {
    background-color: #fff;
    border-radius: 3px;
    margin: 0px;
}

.RadEditor.reInlineEditor .reContentArea {
    background-color: #EBEBEB;
    border: 2px solid lightgray;
    border-radius: 2px;
}

#todoList {
    padding: 4px;
    border-radius: 2px;
}

.RadEditor.reInlineEditor {
    background-color: #EBEBEB;
}

.RadGrid_Bootstrap .rgHeader {
    background-color: #A9A9A9;
}

tr.rgRow,
.RadGrid_Bootstrap .rgSorted {
    background-color: #9E9E9E;
}

tr.rgAltRow,
.RadGrid_Bootstrap .rgAltRow > td,
.RadGrid_Bootstrap .rgAltRow .rgSorted {
    background-color: #c0c0c0;
}

/*.rgAltRow.rgSelectedRow, .rgRow.rgSelectedRow {}*/
/* hover */
.RadGrid_Bootstrap .rgHoveredRow > td {
    background-color: #EBEBEB;
}

div.block-web {
    background-color: #687174;
    color: white;
}

div.page-content,
.ctl00_pageBody,
[id=ctl00_pageBody] {
    background-color: #424a4d;
}

/*span.control-label {
    color: black;
}*/
h2,
h3 {
    color: white;
}

#ctl00_phContent_grdIssueSubTypes,
#ctl00_phContent_grdIssueSubTypes_GridData {
    height: 100% !important;
}

.list-group-item.logItem {
    background-color: darkgray;
}
.list-group-item.logItemAlternate {
    background-color: lightgray !important;
}

.btn-group,
div.media:not(.bg-success) {
    color: black;
}

.img-thumbnail {
    border-color: gray;
    background-color: darkgray;
}
}

@-moz-document domain(forum.ereality.ru) { 
	html, .LoginForm {
	  background: none !important;
	}

	body, #Footer, .UnderFooter {
	  background: none !important;
