}

.content-listing a {
    color: #6e6e6e;
    font-weight: 300;
    font-size: 16pt;
    margin-bottom: -8px;
}

/* Course Ribbons */

.ribbon {
    right: 0px;
    top: 0px;
    overflow: inherit;
}

.ribbon span {
    font-size: 12px;
    color: #fff;
    -ms-transform: rotate(0deg);
    transform: rotate(0deg);
    -webkit-transform: rotate(0deg);
    width: 130px;
    background: #343a3e;
    -webkit-box-shadow: 0 0px 0px 0px #000;
    box-shadow: 0 0px 0px 0px #000;
    position: absolute;
    top: 0px;
    right: 0px;
}


/* Irrelevant Objects */
.disableable.disabled, .heading-wrapper .alert, .greeting-large, .heading-wrapper h3.ng-binding, .heading-wrapper a[class*="help"] {
    display:none;
}
