@-moz-document url-prefix("https://adventofcode.com/") {

body {
	/* besh: #e3e3a6 */
	background-color: #211a30;
	color: #c2cad2;     
     

    font-family: 'Work Sans', sans-serif;

	font-size: 18px;
	line-height: 25px;

}

* {
	scrollbar-width: thin;	
	scrollbar-color: #191122 transparent;
}

article {
	margin-top: 4rem;	
}

article h2,
h2 {
	position: relative;
 	font-size: 28px;
	text-align: center;     
    font-family: 'Roboto Mono', monospace;
	color: #fc2b43;
	text-shadow: 0 0 2px #000000, 0 0 5px #fa10a4bd;
	margin-bottom: 2rem;
}

code {
	margin: 0 .2rem;	
}

header,
.leaderboard-entry,
code,
pre, code {
    font-family: 'Roboto Mono', monospace;
}

.calendar > span,
.calendar .calendar-day,
.privboard-star-unlocked,
.privboard-days > span{
	color: #3a3345;	
}

.calendar {
	width: 76%;
	position:relative;
	padding: 1rem .1rem;
	border: 3px solid #000000;
	background-color: #191122;
	border-radius: 8px;
	margin-top: 6rem;
	overflow:hidden;
}

.calendar > a {
	color: #d6b049;	
}

.calendar .calendar-s {
	color: #382652;	
}

.calendar > a:after,
.calendar > span:after {
	color: #382652;	
}

.calendar-day1::after {content:'            .  .                       .                              .      '}
.calendar-day2::after {content:'    .                          ..       .        .                           '}
.calendar-day3::after {content:'                           .                                                 '}
.calendar-day4::after {content:'                               .         .              ..             ..    '}
.calendar-day5::after {content:'          .    .                .                                            '}
.calendar-day6::after {content:'.                                             .              .         .  .  '}
.calendar-day7::after {content:'           .             .                                   .               '}
.calendar-day8::after {content:'               .                          .                       .          '}
.calendar-day9::after {content:'                              .                    .  .                      '}
.calendar-day10::after {content:' .                                           .        .                      '}
.calendar-day11::after {content:'       .                             .                                   .   '}
.calendar-day12::after {content:'                                                                             '}
.calendar-day13::after {content:'          .     . .                                    .                     '}
.calendar-day14::after {content:'                  .            .                                    .        '}
.calendar-day15::after {content:'               .                           .                                 '}
.calendar-day16::after {content:'                        .        .                      .         .      .   '}
.calendar-day17::after {content:'        .           .                  .                                     '}
.calendar-day18::after {content:'         .        .                .      .          .             .         '}
.calendar-day19::after {content:'  .                                 .          .                             '}
.calendar-day20::after {content:'     .                                  .      .                             '}
.calendar-day21::after {content:'                       .                              .       .              '}
.calendar-day22::after {content:'.      .        .      . .            .                          .  .       .'}
.calendar-day23::after {content:'             .                                               .            .  '}
.calendar-day24::after {content:' .                     .     .            .                    .  .          '}
.calendar-day25::after {content:'        .           .                                 . .         .          '}

header h1 a, header h1 span {
	color: #20b26a;	
	text-shadow: 0 0 2px #3c3232, 0 0 5px #24ac69d1;	
}

pre {
	width: 100%;
}

pre code {
	width: calc(100% - 1.2rem);
	padding: .6rem;
}

.hidden {
	border: none;
}

.hidden::before {
	border: none;
	background-color: #000000df;
}

input[type="text"], textarea,
code::before {
	top: -1px;
	bottom: -1px;
	left: -5px;
	right: -5px;
	border-radius: .3rem;
	border: 2px solid #000000;
	background-color: #191122;
}

main {
	padding-left: 12%;
}

a {
	color: #20b26a;	
}

.supporter-badge {
	color: #ffe355;
	text-shadow: 0 0 2px #000000, 0 0 5px #ffe35563;
}

.stats > a:hover, .stats > a:focus,
input[type="checkbox"] ~ span:hover, input[type="checkbox"] ~ span:focus,
input[type="radio"] ~ span:hover, input[type="radio"] ~ span:focus {
	background: linear-gradient(
		90deg,
		#191122 0%,
		#191122 82%,
		#191122 82%,
		transparent 99%,
		transparent 100%
	);
}

#sidebar {
	margin-top: 5rem;	
}

.stats > span, .stats > span .stats-firstonly, .stats > span .stats-both {
	text-shadow: none
}

.privboard-star-firstonly,
.stats-firstonly {
	text-shadow: 0 0 2px #000000, 0 0 5px #d1d1e3a3;
}

.stats-both,
.leaderboard-daydesc-both,
article em.star,
.day-success,
.calendar a.calendar-verycomplete .calendar-mark-verycomplete,
.calendar a.calendar-complete .calendar-mark-complete, .calendar a.calendar-verycomplete .calendar-mark-complete,
header .user .star-count,
.privboard-star-both {
	color: #ffe355;
	
	text-shadow: 0 0 2px #000000, 0 0 5px #ffe355e6;
}

/* width */
::-webkit-scrollbar {
  width: 8px;
}

/* Track */
::-webkit-scrollbar-track {
  background: #12121400;
}

/* Handle */
::-webkit-scrollbar-thumb {
  background: #3a3d45;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #4e5558;
}

article.day-desc li {
	margin-bottom: .3rem;	
}

@media(max-width:1250px) {
	main {
		padding-left:0;	
	}
	
	.calendar {
		margin: -3px;	
	}
	
	#sidebar {
		margin-left: .2rem;	
	}
}
}

@-moz-document regexp("https://adventofcode.com(/(2\\d{3}/?)?)?") {

main::before {
	content: 'Advent Of Code';
	position:absolute;
	font-size: 2rem;
    font-family: 'Roboto Mono', monospace;
	top: 6.2rem;
	padding: .7rem;
	color: #9999cce8;
	text-shadow: 0 0 2px #05050bfa, 0 0 5px #9999cca1;
}

main::after {
	width: 76%;
	content: 'Theme by TonyStr';
	color: #9999cc29;
	text-shadow: 0 0 4px #9999cc08;
	display:block;
	text-align: right;
	font-size: 1rem;
    font-family: 'Roboto Mono', monospace;
	margin-top: -.3rem;
	margin-left: -.3rem;
}
}

