@-moz-document url("https://www.thewatchcartoononline.tv/") {
/* Ads and Annoyances */
html body br{display:none !important}
html body > div:not(.row){display:none !important}
html body .row::before,html body .row::after{display:none !important}
html body > .row:nth-of-type(3) > .twelve.columns > .row > .eight.columns > #sidebar_today > div{display:none}


html body .row{width:unset;min-width:unset;max-width:unset;margin:unset;background:transparent;border:none}
html body .column, html body .columns{float:unset;min-height:unset;padding:unset}
html body .row .twelve{width:unset}
html body .row .row{width:unset;max-width:unset;min-width:unset;margin:unset}
html body .row .eight{width:unset}
html body .row .four{width:unset}
html body [class*="column"] + [class*="column"]:last-child{float:unset !important}

/* Main */
html body{color:rgb(200,200,200);background:rgb(30,34,36);margin:0;display:flex;flex-direction:column;flex-wrap:nowrap}

html body > #btnTopPage{bottom:10px;background:rgb(200,50,50);font-size:16px;font-weight:400;height:40px;width:120px;padding:4px 10px}

/* Nav bar */
html body > .row:nth-of-type(1){display:flex;flex-direction:row;flex-wrap:nowrap;position:fixed;top:60px;left:0;right:0;z-index:2;border-bottom:2px solid rgb(40,44,46)}
html body > .row:nth-of-type(1) > .header-top{height:30px;background:rgb(40,44,46);padding:0;margin:0;color:transparent;font-size:0;line-height:unset;width:max-content}
