    left: calc(50% - 5px);
    border-radius: 50%;
    background: var(--main-color);
    animation-name: musictrack;
    animation-duration: 2.5s;
    animation-iteration-count: infinite;
    animation-timing-function: ease-out;
    animation-fill-mode: both;
