@-moz-document domain("locajogos.com") {
/* Topo */
div[class*="topo"],
nav,
.pages-nav.content {
    font-family: 'Open Sans', sans-serif;
    background-color: #222 !important;
    box-shadow: 0 0 5px #757575 !important}

/* Fundo */
body {
    margin-top: 50px;
    background: #E3E3E3;
    background: -moz-linear-gradient(left, #d5d5d5 -25%, #e9e9e9 50%, #d5d5d5 75%);
    background: -webkit-linear-gradient(left, #d5d5d5 -25%, #e9e9e9 50%, #d5d5d5 75%);
    background: linear-gradient(to right, #d5d5d5 -25%, #e9e9e9 50%, #d5d5d5 75%);
}

/* Catálogo Topo */
div [class="alert alert-info text-center"]
    {font-family: 'Open Sans', sans-serif; font-weight: 700; letter-spacing: 1px; color:#f1f1f1; background-color:#1B5C9E; border-radius: 5px; border-color:#002d5b !important;}

/* Roleta e Mimosa Topo */
div [class="alert alert-info text-center col-lg-12"]
    {font-family: 'Open Sans', sans-serif; font-weight: 700; letter-spacing: 1px; color:#f1f1f1; background-color:#1B5C9E; border-radius: 5px; border-color:#002d5b !important;}

/* Pesquisa */
div [class="input-group col-sm-5"]
{ width: 70%; margin-top: 5px; margin-bottom: 10px; margin-left: 26px; margin-right: 0px;}
        
div [class="col-sm-3"] 
{ width: 25%; margin-top: 2px; margin-bottom: 10px; margin-left: 0px; margin-right: 0px; padding: 3px 12px;}

[class*="border"],
div,
::before,
::after {
    border-color: #9a9a9a !important }

/* Botão Ajuda */
#btnAjuda {
	font-weight: 700;
    letter-spacing: 1px;
    display: inline-block;
    padding: 0px;
	color: #ffffff;
	background-color: #1B5C9E;
	width: 110px;
	height: 110px;
	line-height: 100px;
	border-radius: 50%;
	border: double 9px #ffffff;
	text-align: center;
	vertical-align: middle;
	overflow: hidden;
	transition: .9s; }
/* Botão Ajuda End */

#content-wrapper,
.content-wrapper,
.content-wrapper::after,
.article,
.flex_box > div,
.Box-header,
.select-menu-header,
.modal,
#styleguide-v2 #wrapper {
    background-color: #202020 !important
}

#myCarousel {
    background-color: black;
}
.tituloNoticia {
    background: black;
}
#divMateria {
    background-color: black;
    /*height:400px;
	*/
}
.materiaContainer {
    margin: 2em 0;
    padding: 0.4em;
    /*overflow:hidden;
	*/
    background-color: #d0d4b6;
    max-height: 400px;
}
.materiaDivImagem {
    background-color: black;
    padding-top: 2em;
    padding-bottom: 2em;
}

.materiaImagem {
    width:6600px;
	height:200px;
    margin-top:100px;
}
.materiaDivConteudo {
    background-color: red;
}
.materiaTitulo {
    color: yellow;
    font-weight: bold;
}
.alturaMateria {
    min-height: 600px;
}
.ConteudoFilho {
    min-height: 800px;
    margin-top: 100px;
}
.AlinhaV {
    vertical-align: middle;
}
.AlturaBase {
    margin-top: 10px;
}
.GridMinhasContas {
    width: 80px;
    height: 100px;
}
/*Box Jogo*/
figure.snip0078 {
    color: #414141;
    border-radius: 6px;
    background-color: #cfcfcf;
    position: relative;
    float: right;
    margin-top: 15px;
    /*Botões Favorito e Jogado*/
    margin-bottom: 16px;
    margin-left: 20px;
    /* Tamanho Box */
    width: 252px;
    height: 360px;
}
/* Capa Jogo */
figure.snip0078 img.cardCapa {
    opacity: 1;
    max-width: 100%;
    border: 10px solid #000000;
    -webkit-transition: all 0.3s;
    transition: all 0.3s;
    -webkit-transform: scale(0.90);
    transform: scale(0.90);
    -webkit-transform-origin: 0 0;
    transform-origin: 0 0;
    /*Posição Capa*/
    margin-top: -20px;
    margin-left: 20px;
    /*Sombra Capa*/
    box-shadow: 0 1px 4px 1px #40404038;}
    /*Categoria Jogo*/
    div [class="h6"]
    {font-family: 'Open Sans', sans-serif; text-align: center; font-size: 13px; color: #414141;}

/* Platina, Crossbuy e Sombra Jogo */
figure.snip0078 figcaption {
    bottom: 0px;
    right: 0%;
    position: absolute;
    background: #000000;
    padding: 0px;
    -webkit-transition: all 0.2s;
    transition: all 0.2s;
    -webkit-transform: translateY(0);
    transform: translateY(0);
    box-shadow: 0 9px 9px -6px #4040406E;
