@-moz-document url-prefix("https://zhuanlan.zhihu.com/write") {
/*知乎专栏 写文章页面 工具提示气泡复位*/
span div .TooltipContent-wrapper {
    background: #0000;
    position: relative;
    left: 0px;
    top: 0px;
    
}
span div div.TooltipContent {
    background: rgba(26,26,26,0.8);
    color: #fff;
    position: absolute;
