/* Nomination Management */
#nom-search-title {
    background-color: rgb(46, 46, 46);
    color: #fff;
}

.nomination-title,
.nomination-category-header {
    color: #fff
}

#nom-description,
.nomination-category-body {
    color: #d0d0d0;
}

/* Tags */
.nomination-status--voting,
.nomination-status--queue {
    color: #d0d0d0;
    background: rgba(208, 208, 208, .169);
}

.nomination-status--duplicate {
    color: #ff2020bf;
    background: rgba(255, 0, 0, .2);
}

.nomination-status--upgrade {
    color: #00d2ee;
    background: rgba(0, 210, 238, .2);
}

.button-secondary.button-upgrade {
    color: #fff;
    background: rgb(0, 210, 238);
}

.button-secondary.button-upgrade:hover {
    color: #fff;
    background: rgb(1, 155, 176);
}

/* Buttons */
button[disabled],
button,
.button-secondary {
    color: #c9c9c9;
    background: rgb(46, 46, 46);
}

button[disabled]:hover,
button:hover,
.button-secondary:hover {
    background: #3d3d3d;
}

.item-edit {
    filter: invert(50%);
}

/* Showcase */
#gallery-info {
    background-color: rgb(46, 46, 46);
}
}

@-moz-document domain("duelingnexus.com")
{
/*change this link to change the background*/

  html ,#game-rps-container
  {
    background: url(https://i.ytimg.com/vi/WewGCmr0ivc/maxresdefault.jpg)!important;
  }

  .game-field-zone-activate
  {
    content: url("https://i.ytimg.com/vi/WewGCmr0ivc/maxresdefault.jpg");
  }

  *
  {
    border-radius: 0px 10px 10px 10px;
  }

  .game-field-card
  {
    border-radius: 0;
  }

  .game-field-zone
  {
    border-radius: 0;
  }

  #editor-main-deck, #editor-extra-deck, #editor-side-deck
  {
    border: 1px solid #ffffff47;
  }

  #navbar-left , #navbar-right
  {
    position: fixed;
    z-index: 5;
  }

  #navbar-middle
  {
    position: fixed;
    padding-left: 35%;
    top: 0%;
  }

  .game-selection-card
  {
    margin: 1vh 1vw 1vh 1vw;
    z-index: 7;
  }

  .game-field-card
  {
    z-index: 10;
  }

  .game-field-stats-text
  {
    color: #fff !important;
    z-index: 3;
  }

  .game-field-zone-content
  {
    color: #fff;
    z-index: 5;
  }

  .game-card-negated
  {
    z-index: 5;
  }

  .game-field-zone-activate
  {
    z-index: 8;
    position: absolute;
  }

  #game-option-list
  {
    padding: 0.5vw;
  }

  #game-option-list .engine-button
  {
    margin: 0.25vw;
    padding: 0;
    position: relative;
    bottom: 0;
  }

  #game-tooltip
  {
    color: white;
    font-size: 1.05vw;
    background: radial-gradient(#00000070, #0a0202);
    border: 1px dashed #000;
    padding: 1vh;
    z-index: 7;
    position: fixed;
  }

  #downloadDeck
  {
    background-color: #19660c;
  }

  #card-description-box
  {
    background: url("https://imgur.com/XUYfjMk.png");
  }

  #game-alert-window,
#options-window,
#game-results-container,
#game-message-window,
#game-waiting-menu,
#game-selection-window,
#game-yesno-window,
#game-position-window,
#game-option-window,
#game-announce-window,
#game-announce-card-window
  {
    background-color: #00000080;
    border-color: #fff;
    border-width: 15px 1px 1px 3px;
    border-radius: 15px;
    left: -15vw;
    top: 40vh;
  }

  #game-end-window
  {
    background-color: #00000080;
    border-color: #fff;
    border-width: 15px 1px 1px 3px;
    border-radius: 15px;
  }

  #game-room-container
  {
    width: 60vw;
    min-width: 10px;
    padding-bottom: 0px;
    padding-top: 0px;
    padding-left: 0px;
    padding-right: 0px;
  }

  #game-room-players
  {
    width: 40vw;
    min-width: 28px;
    padding: 2vh 2vw 2vh 1vw;
    margin-top: 20px;
  }

  #game-room-information
  {
    width: 16vw;
    min-width: 260px;
    padding: 2vh 2vw 2vh 1vw;
  }

  #game-selection-window
  {
    overflow-y: scroll;
    height: 25vw;
    width: 40vw;
    z-index: 6;
    left: 15vw;
    top: 30vh;
  }

  #game-deck-selection
  {
    border: solid #fff;
    border-width: 12px 1px 1px 3px;
    border-radius: 11px;
    overflow-y: scroll;
    height: 300px;
    width: 450px;
    min-width: 120px;
    margin-top: 7px;
    padding: 5px;
    z-index: 2;
    position: absolute;
  }

  #options-area
  {
    position: fixed;
    right: 0.8vw;
    bottom: 25qpx;
  }

  #options-window
  {
    position: fixed;
    height: 200px;
    width: auto;
    overflow-y: visible;
    z-index: 10;
    left: 65vw;
    top: 60vh;
  }

  #game-siding-column
  {
    height: 37.67vw;
    width: 33.33vw;
  }

  #game-siding-column-2
  {
    border: 1px;
    height: 37.67vw;
    width: 37.67vw;
  }

  #game-siding-done
  {
    height: 4vw;
    width: 100%;
    position: relative;
    top: 5vw;
  }

  .game-field-zone
  {
    background: repeating-radial-gradient( #0000007d , #000000a6 );
    border: 3px solid #fff0;
    border-style: outset;
  }

  #my-profile , #home-area ,
#duel-area , #decks-area
, #gamelist-area
  {
    background-color: #010101b5;
  }

  #news-area
  {
    background: #000000b5;
  }

  .adsbygoogle
  {
    opacity: 0;
  }

  body
  {
    font-size: 14px;
  }
}

@-moz-document domain("duelingnexus.com")
{
/*delete this code section if you are having problems with the 3D game field */

  #game-selection-window
  {
    transform: translate(-19vw , -11vh);
  }

  #game-yesno-window
  {
    transform: translate(-4vw);
  }

  #game-container
  {
    perspective: 2100px;
  }

  #game-column
  {
    transform: rotatex(45deg) translatex(-20px) scale(0.9,1.02);
  }

  #game-action-menu
  {
    transform: translate(-27.5vw, -3vh);
  }

  #game-tooltip
  {
    transform: translatex(-27vw);
  }

  .game-avatar-area
  {
    transform: scale(1.15);
  }

  #game-rps-container , #game-rps-result
  {
    perspective: 1700px;
  }

  #game-rps-scissors
  {
    transform: rotatex(50deg) scaley(1.3);
    padding: 5px;
  }

  #game-rps-paper
  {
    transform: rotatex(50deg) scaley(1.3);
    padding: 5px;
  }

  #game-rps-rock
  {
    transform: rotatex(50deg) scaley(1.3);
    padding: 5px;
  }
}

