:root {
  --green: rgba(152, 190, 101, .800);
  --magento: rgba(198, 120, 221, .800);
  --blue: rgba(97, 175, 239, 1.000);
  --blue-border: rgba(51, 102, 153, 1.000);
  --bg-grey: rgba(40, 44, 52, 1.000);
  --bg-grey2: rgba(50, 56, 66, 1.000);
  --bg-grey2-hover: rgb(62, 70, 82);
  --bg-side: rgba(33, 37, 43, 1.000);
  --red: rgba(224, 108, 117, 1.000);
  --title-yellow: rgba(209, 154, 102, 1.000);
  --title-yellow2: rgba(209, 154, 102, .700);
  --highlight-yellow: rgba(229, 192, 123, 1.000);
  --text-grey: rgba(187, 187, 187, .700);
  --text-grey2: rgba(187, 187, 187, 1.000);
  --visited-yellow: rgb(132, 78, 28);
  --button-text: rgba(144, 139, 129, 1.000);
}
html, body {
  font-family: -apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,Oxygen-Sans,Ubuntu,Cantarell,"Helvetica Neue",sans-serif;
  background-color: var(--bg-grey);
}

/* Links */
a {
	color: var(--blue);
}

/* Title */
.cZRnrv,
._eYtD2XCVieq6emjKBH3m {
  color: var(--title-yellow);
}
.jjmktr .SQnoC3ObvgnGjWt90zD9Z:visited .yk4f6w-0 {
  color: var(--visited-yellow);
}

/* Time */
._3jOxDPIQ0KaOWpzvSQo-1s,
._1sA-1jNHouHDpgCp1fCQ_F,
.eiLEEO ._3jOxDPIQ0KaOWpzvSQo-1s,
.yBpHA{
  color: var(--green) !important;
}

/* Username */
._23wugcdiaj44hdfugIAlnX,
.cjUVFb ._2tbHP6ZydRpjI44J3syuqC,
.eJfCYs,
._31VWB3vSkv19o3I7RVE710 {
  color: var(--magento) !important;
}

._23wugcdiaj44hdfugIAlnX:not(.oQctV4n0yUb0uiHDdGnmE)._1uBAPhkb8ZOA-9p_B6inAR,
.eQmyey /*username*/, .gbqIEy /*mic*/ {
  color: var(--red) !important;
  font-weight: 700;
}

/* Text */
.eZgKdE,
.ccJBvn,
._1qeIAgB0cPwnLhDF9XSiJM {
  color: var(--text-grey);
}

/* TS Code block, comments code block*/
._3GnarIQX9tD_qsgXkfSDz1,
._3GnarIQX9tD_qsgXkfSDz1>._34q3PgLsx9zIU5BiSOjFoM {
  background: var(--bg-grey) !important;
  color: var(--green) !important;
}

}

