:root {
	--base-color: #131313; /* Change this to adjust base color */
	--white: #FFFFFF;
	--pure-black: #000000;
	--blacker: #444444;
	--classic-grey: #cccccc;
	--grey: #2b2b2b;
	--light-grey: #555555;
	--dark-grey: #333333;
	--darker-grey: #222222;
	--darkest-grey: #111111;
	--light-grey: #999999;
	--lighter-grey: #888888;
	--lighten-grey: #777777;
	--warning: #272727;
    --admin: #af8282;
    --meneo: #9a5d40;
	--brand:#ff6600;
}

/* bordes */

div,
a,
span {
	border: none !important;
}

button,
select,
form {
	border-color: var(--blacker) !important;
}


/* fondos */

.menu01-itemsl span.button-new,
.widget-popular-links-slider .link.slick-slide.slick-active,
.section-profile .contents-layout,
.pages a,
.pages span,
.news-summary .warn,
