fieldset,
.comment-body,
.topbox,
.header-top-wrapper,
.comment-header,
.comment-header a,
.comment-header span,
.comment-header strong,
select,
.sneaker-legend {
	background: var(--base-color) !important;
}


/* fondos_secundarios */

.news-details a:link, .news-details a:visited { border-radius:10px; }
.sidebox .header { border-radius:0; }
.sidebox {
    background:var(--grey);
    margin-top:0;
