@description    Clear Lost Ark Map by LRGBX
@author         LRGBX
==/UserStyle== */

@-moz-document url-prefix("https://lostark-es.com/map") {
	nav.sidebar {
		display: none;
	}

	div#main-page {
		height: 100%;
		width: 100%;
		display: flex;
		flex-flow: column nowrap;
	}

	div#main-page > * {
		display: none !important;
	}

	div#main-page > nav,
	div#main-page > div.container-fluid {
		display: block !important;
		position: relative;
		top: 0;
		left: 0;
		right: 0;
		bottom: 0;
	}

	div#main-page > nav {
		height: auto !important;
	}

	div#main-page > div.container-fluid {
		height: auto;
		flex: 1 1 100%;
	}

	div#main-page > div.container-fluid > .row {
		position: absolute;
		top: 0;
		left: 0;
		right: 0;
		bottom: 0;
	}

	div#mundo {
		height: 100%;
		margin-top: 0;
		position: relative;
	}

	div#mundo > #areasDet {
		height: auto !important;
		max-height: 100% !important;
		margin: 0;
	}

	div#mundo > #areasDet > #titAreas {
		display: none;
	}

	div#controller {
		top: 25px;
		left: 25px;
		position: absolute;
	}

	div#menu.menu-ui {
		position: absolute;
		top: 25px;
		right: 25px;
		bottom: 5px;
		height: auto;
		padding: 10px;
		z-index: 99999;
	}

	div#menu.menu-ui > ul {
		margin: 0;
		width: 100% !important;
	}

	div#menu-cont {
		position: relative;
		left: 0;
		top: 0;
		right: 0;
		bottom: 0;
		height: auto;
		padding: 5px 5px 26px;
		display: flex;
		flex-flow: row wrap;
		justify-content: center;
	}

	div#menu-cont > a.collapsible {
		margin: 5px;
	}

	div#menu-cont > a.collapsible.soon {
		display: none;
	}

	div#menu-cont > div.content {
		width: auto;
		bottom: 5px;
	}

	div#menu-cont > div.content li {
		margin: 0;
		padding: 0 9px;
	}

	div#menu-cont > div.content li:last-of-type {
		border-right: none;
	}
}

