data.css:
@-moz-document domain("uesp.net") {
#content.mw-body
{
    font-size: 1rem;
}

#mw-content-text
{
    overflow-x: auto;
}

@media only screen and (max-width: 1549px) {
    #column-one > #p-cactions.portlet[role="navigation"]
    {
        left: initial;
        right: calc(50vw - 600px - 1vw);
        top: 23px
    }
}

@media only screen and (min-width: 1550px) {
    #content.mw-body
    {
        max-width: 1100px !important;
        margin: auto;
        margin-top: 40px;
    }

    #p-cactions
    {
        position: fixed;
        left: calc(50vw - 500px - 80px);
        top: 18px
    }
    
    .portlet
    , #p-logo
    {
        position: relative;
        left: calc(50vw - 500px - 250px);
        top: -156px;
    }
}
}

@-moz-document domain("deezer.com")
{
@font-face
  {
    font-family: 'Open Sans';
    src: local('Open Sans'), local('OpenSans'),
url(https://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
  }

  *
  {
    font-family: "Open Sans" !important;
  }

  body
  {
    margin-top: 110px !important;
  }

  #page_topbar
  {
    top: 150px;
  }

  #page_topbar .popper-search { 
      top: 55px
  }

  #page_sidebar
  {
    padding-top: 110px; height: 100vh
  }

  .page-main
  {
    top: 100px;
  }

  .player-track
  {
    position: fixed !important;
    padding: 13px 5px 10px 115px !important;
    left: 0;
    top: 0;
    text-shadow: 1px 1px 1px rgba(0,0,0,.5);
    height: 111px;
    width: 100%;
    font-family: 'Open Sans', sans-serif !important;
    font-weight: 300 !important;
    z-index: 9999 !important;
    background: /*[[BGCOLOR]]*/ url(data:image/gif;base64,) 15px 15px no-repeat;
    background-size: 90px 80px;
  }

  .ads
  {
    display: none !important;
  }

  .track-seekbar
  {
    display: none;
  }

  .track-actions
  {
    display: none;
  }

  .page-player .track-title
  {
    font-size: 48px;
    line-height: 80px;
    Color: #fff;
    font-weight: 300;
    letter-spacing: -.06em;
  }

  .page-player .player-track .track-container
  {
    margin: auto;
    max-width: 100%;
    width: 100%;
  }

  .page-player .player-track .track-link:hover
  {
    text-decoration: none;
  }

  .page-player .player-track .track-label
  {
    display: none;
  }

  .marquee-gradient-left
  {
    background-image: -webkit-gradient(linear,left top,right top,from(/*[[BGCOLOR]]*/),to(hsla(0,0%,100%,0)));
    background-image: linear-gradient(90deg,/*[[BGCOLOR]]*/,hsla(0,0%,100%,0));
  }

  .marquee-gradient-right
  {
    background-image: -webkit-gradient(linear,left top,right top,from(hsla(0,0%,100%,0)),to(/*[[BGCOLOR]]*/));
    background-image: linear-gradient(90deg,hsla(0,0%,100%,0),/*[[BGCOLOR]]*/);
  }
}

