    color: #000;    
}

.schedule-card__footer .areena-link::before {
	/*border: 1px solid #ccc; */
    border-radius: 50%;
    /* border-color: #ccc; */
    margin-right: 2px;
    margin-left: 0px;
    padding-left: 4px;
    width: 13px;
    height: 16px;
    min-width: 0px;
    padding-bottom: 2px;
}


.areena-link--ondemand::before, .areena-link--current::before {
    background: none;
    content: "▶";
}
.areena-link--ondemand::before:hover, .areena-link--current::before:hover{
    background: none;
    color: red;
}   

