.yle-header-ad{
    display:none;
}

.areena-status--current {
    background: none;
}
.areena-status--upcoming::before {
    display: block;
    background-color: #11b4c7;
    padding-left: 3px;
    width: 10px;
    border-radius: 50%;
    margin-right: 10px;
    font-size: 10px;    
    background:none;
    color: #fff;
    
}
.areena-status--upcoming::before {
    content: "▶";
    background:none;
}
.areena-status--current::before {
    content: "▶";
    display: block;
    background-color: #11b4c7;
    padding-left: 3px;
    color: white;
    width: 10px;
    border-radius: 50%;
    margin-right: 10px;
    font-size: 10px;
}
}

