:root {
    --gray1: #ccc;
    --gray2: #efefef;
    --green: #5cb85c;
}

*::-webkit-scrollbar-track {
    background-color: rgba(255, 255, 255, .08) !important;
    border-radius: 0 !important
}

*::-webkit-scrollbar {
    width: 10px !important;
    background-color: rgba(13, 13, 13, 0) !important
}

*::-webkit-scrollbar-thumb {
    border-radius: 0px !important;
    background-color: var(--gray1) !important;
}

*::selection {
    color: #fff;
    background: var(--green)
}

body *{
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
}

a {
    color: #0263b7;
}

p {
	font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
}

h2 {
    font-style: inherit!important;
    font-size: 24px;
}

table, td {
    font-size: 14px;
    border-width: 0px!important;
}

td[value*="design"] {
    background-color: rgb(92, 184, 92)!important;
}

form table div table>tbody>tr:nth-child(even), select option:nth-child(even) {
    background-color: var(--gray2);
}

table>tbody>tr:last-child {
    background-color: white;
}

form table div table {
	margin-bottom: 20px;
}

select {
    margin-left: 5px;
    padding: 3px 5px;
    border: none;
}

option[selected="selected"] {
    background-color: rgb(92, 184, 92)!important;
    color: white;
    padding: 3px;
}

table#cssTable tr {
     background-color: white!important;
}

th {
    border-right: 1px solid #e27e3a;
    text-align: center;
    padding: 5px 10px!important;
    text-transform: uppercase;
    height: 25px;
    background-color: #ff954d;
    font-weight: normal;
    line-height: 1.2;
}

th:first-child {
    border-radius: 0px 0px 0px 0px;
}

th:last-child {
    border-radius: 0px 0px 0px 0px;
}

.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th, .table>thead>tr>th, tr {
    border-bottom: none;
    border-top: none;
}

.sortable tr td, table tr td {
    padding: 4px 8px;
    line-height: 1.6;
}

table tr td img {
    width: auto!important;
    height: auto!important;
}

tfoot {
    border-top: 2px solid #f5f5f5;
}

.btn-primary {
    background: #1267a7;
    background-image: none;
    box-shadow: none;
    border-color: #1267a7;
}

.btn-default, .btn-default:focus, .btn-default:hover {
    background: #f9f9f9;
    background-image: none;
    box-shadow: none;
    border-color: #e6e6e6;
