}

.btn-warning, .btn-warning:hover {
    background: #f57e27;
    background-image: none;
    font-weight: normal;
    text-shadow: none;
    box-shadow: 0px 0px 0px 8px white;
    border: 0px solid white;
}

#loginform {
    padding-top: 0!important;
}

/* box 1,2 */
div.topAthletes, .box li{
    font-size: 14px;
}
.listBoxWrapper tr{
    background-color: white!important;
}
.box, .aboutPage {
    box-shadow: 0px -2px 0px 0px #e2e2e2;
    border-radius: 0;
}
#header .rmItem .rmLink, .box h3 {
    top: -45px;
    background: white;
    background-image: none;
