}

/* footer */
#ctl00_divSupporthcm {
    background: var(--gray2);
    padding: 10px 15px;
    border-radius: 5px;
}

#ctl00_divSupporthcm br {
    line-height: 0;
}

#ctl00_divSupporthcm span {
    background: transparent!important;
}

/* new list */
