@-moz-document url-prefix("https://app.astraltabletop.com/widget/game") {
/*
    Astral, Chat, delayed dice menu
    Modifies: Dice menus in the in-game chat
    Shrinks the long formula into a "click" button,
    enables Actions to be menus of die rolls by skill or attribute
*/

/*
div.chat-message
  span.message-container
    span.action-box     has title
    table or div        user content
      span.enabled      click to roll
      span.roll
        span.value
*/

div.chat-message .message-container {
    font-family: verdana;
    font-size: 11px;
}
div.chat-message .message-container .action-box {
    font-size: 20px;
}

div.chat-message span.enabled {
    /* this makes a box */
    display: inline-block;
    width: 30px;
    height: 18px;
    overflow: hidden;

    /* this lines it up */
    position: relative;
    top: 4px;

    /* tiny print */
    font-size: 9px;
    font-style: normal;
    padding: 1px 1px;
    color: yellow;
}


}

@-moz-document url("https://app.astraltabletop.com/widget/game") {
/*
    Astral, Chat, color unicode dice
    Modifies: bold and italic die rolls in chat
    The unicode dice characters are too small, this makes
    them larger and colors them.
    white d6: *!!(1d[⚀,⚁,⚂,⚃,⚄,⚅])*
    red d6: **!!(1d[⚀,⚁,⚂,⚃,⚄,⚅])**
    blue d6: **_!!(1d[⚀,⚁,⚂,⚃,⚄,⚅])_**
    green d6: _**!!(1d[⚀,⚁,⚂,⚃,⚄,⚅])**_
*/

div.chat-message em .value {
    color: white;
    font-style: normal;
    font-size: 36px;
}
div.chat-message strong .value {
    color: red;
    font-weight: normal;
    font-size: 36px;
}
div.chat-message strong em .value {
    color: lightblue;
    font-style: normal;
    font-weight: normal;
    font-size: 36px;
}
div.chat-message em strong .value {
    color: seagreen;
    font-style: normal;
    font-weight: normal;
    font-size: 36px;
}

}

