@-moz-document regexp(".*:\\/\\/((app\\.|)roll20\\.net([\\/]?|\\/editor.*|))$") {
p, body .content.note-editor.gmnotes, .content.note-editor.notes {
        font-family: Georgia;
        font-size: 1.5rem;
}

h1, h2, h3 {
    font-family: Mrs Eaves !important;
    color: #58170D;
    font-weight: bolder;
    font-variant: small-caps;
    text-transform: capitalize;
}
blockquote {
    box-sizing: border-box;
    margin-bottom: 1em;
    margin-top: 1em;
    padding: 5px 10px;
    background-color: #e0e5c1;
    border-left: 0px; 
    border-style: solid;
    border-width: 1px;
    box-shadow: 1px 4px 14px #888;
}
h1 {
    font-size: 4rem !important;
        margin-bottom: 1em;
}
h2 {
    font-size: 3rem !important;
}
h3 {
    font-family: sans-serif;
    font-variant: small-caps;
    font-size: 2rem;
    text-transform: capitalize;
    color: #58170D;
    margin: 10px 0px 1px 0px;
border-bottom: 1px solid #58170D;
}
}

@-moz-document regexp(".*:\\/\\/((app\\.|)roll20\\.net([\\/]?|\\/editor.*|))$") {
#secondary-toolbar .tokenactions .btn, .btn.btn-default {
    padding: 0px 3px 0px 3px;
    margin-top: 2px;
    margin-bottom: 0px;
    margin-right: 1px;
}
#secondary-toolbar { 
background-color: rgba(0, 0, 0, 0.44) !important;
}
}

@-moz-document domain("app.roll20.net") {
#page-toolbar {
    height: 270px;
}
#page-toolbar .container {
    height: 270px;
    white-space: normal
}
#page-toolbar .height {
    height: 270px!important
}
}

@-moz-document domain("app.roll20.net") {
.textchatcontainer a[href] {
    background-color: transparent;
    padding: 0px;
    color: #ce0f69;
    display: inline-block;
    border: none;
}
.sheet-rolltemplate-npc, .sheet-rolltemplate-npcaction .sheet-container, .sheet-rolltemplate-npcatk, .sheet-rolltemplate-npcdmg, .sheet-rolltemplate-traits {
    width: 95%;
    border: 1px solid;
    background-color: #ffffff;
    padding: 5px;
}
}

/*macro quickbar */
@-moz-document domain("app.roll20.net") {
#macrobar {
  height: 2.3em !important;
  width: 92% !important;
  background-color: rgba(0, 0, 0, 0.44) !important;
  text-align: center !important;
  margin-left: 60px !important;
}
div.macrobox button.btn {
    padding: 0 4px 0 4px;
    font-size: 1.5rem;
    line-height: 1.8rem;
    margin: 0px 0 2px 0;
}
#macrobar_macros {
  white-space: normal !important;
  margin-left: 1% !important;
  margin-right: 1% !important;
  padding-bottom: 0px !important;
}
#macrobar .macrobox {
    margin: 1px 5px 1px 5px ;
}
#macrobar div.macrobox:hover, #macrobar div.macrobox.ui-sortable-helper {
    padding-right: 12px;
    margin-right: -7px;
    border-radius: 3px;
    background-color: #999;
    cursor: move;
}
}

@-moz-document domain("app.roll20.net") {
#initiativewindow {
    padding: 1px 1px;
    overflow-x: auto
}
#initiativewindow ul li {
    padding: 1px;
}
#initiativewindow ul li {
    min-height: 15px
}
#initiativewindow ul li img {
    max-width: 20px;
    max-height: 18px;
}
#initiativewindow ul li span.initiative {
    font-size: 18px;
    padding: 1px;
    min-height: 12px
}
#initiativewindow ul li span.name {
    font-size: 14px;
    padding-top: 1px
}
#initiativewindow input {
    font-size: 12px;
    height: 12px;
}
.ui-dialog-titlebar {
    padding: 1px 5px;
    font-size: 8px;
}
.ui-dialog .ui-dialog-title {
    margin-top: 1px;
    margin-bottom: 1px;
    padding: 1px
}
.ui-dialog .ui-dialog-titlebar-close {
    top: 50%;
    width: 19px;
    margin: -10px 0 0 0;
    padding: 1px;
    height: 6px;
    font-size: 7px;
    line-height: 7.5px;
}
.ui-dialog .ui-dialog-titlebar-close span {
    display: block;
    margin: 1px;
    text-indent: 9999px
}
.ui-dialog .ui-dialog-buttonpane {
    border-width: 1px 0 0 0;
    margin: .05em 0 0 0;
    background-color: #f5f5f5;
    padding: 1px 10px 1px;
}
.ui-dialog .ui-dialog-buttonpane button {
    margin: .05em .04em .05em 0;
}
}

