/* Lys tekst 2 + ikoner */
.header__icon-btn, .dr-egp-text__text, .epg-timeline__time, path.svg-arrow, .drtv-drop-select__arrow-icon {
    color: rgba(255,255,255,0.6) !important;
    fill: rgba(255,255,255,0.6) !important;
