    background-color: rgba(255,255,255,1) !important;
    color: #000000 !important;
}

/* Hvid fill */
.profile-circle__lock svg {
    fill: #ffffff !important;
}

/* Hvid box-shadow */
.digit-input {
    box-shadow: inset 0 -2px 0 -1px rgba(255, 255, 255, 0.8) !important;
}

/* Hover + active tekst */
.primary-nav__entry--active a, a:hover, .d1-drtv-season-list__item--active .d1-drtv-season-list__link {
    color: #ffffff !important;
}

/* Lys baggrund 1 */
.primary-nav__entry--active:before, .cta-btn--secondary-black {
    background-color: rgba(255,255,255,0.8) !important;
}

/* Kategorinavn header image justering */
/*
.h9__picture:before {
    content: ' ';
