  }

  .kr-indented.block-level-5, .kr-olist.block-level-5, .kr-ulist.block-level-5
  {
    border-left-width: 128px;
  }


/*
___________
Typeface
___________
*/

/*COLORS!*/

/*fonts*/

.kr-red-dark-fg {
    color: #F46A54;
    fill: #F46A54;
}

