/* SCROLL BAR */
::-webkit-scrollbar {
  width: 15px;
}
/* Track */
::-webkit-scrollbar-track {
  background: rgb(28, 30, 31); 
}
/* Handle */
::-webkit-scrollbar-thumb {
  background: #2a2c2e; 
}
/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
