.c-toggleTrigger--searchBar,
.l-searchBarToggle {
	background-color: rgba(255, 255, 255, .3)
}

.c-toggleTrigger--searchBar {
	border-radius: 2mm 2mm 0 0;
	margin: 0 1mm
}

.l-searchBarToggle {
	border-radius: 3mm
}

.u-gapBottom--sm,
.u-gapBottom--lg {
	overflow: hidden
}

.u-gapBottom--lg {
	margin: 0 0 2mm
}

.l-chosen .u-gapBottom--lg {
	background: #ccc
}

/* event */
.l-selectedThumb--specialTopic {
	background-size: cover;
	border-radius: 1.5mm;
	box-shadow: 0 0 0 1mm #aaa
}

.u-gapBottom--lg .l-selectedList {
	background-color: #e6e3dd;
	padding: 0 3mm
}

.c-selectItem {
	padding: 6px 0
}

/* footer */
.l-footer {
	width: 100vw;
	box-shadow: 0 0 2mm #888
}

.l-footerLinks {
	font-size: 0;
	display: block;
	text-align: center
}

.l-footerLinks>li {
	display: inline-block;
	margin: 0
}

.l-footer__copyright .list>span>a>img {
	margin-right: 1mm;
}

.u-gapRight--max {
	margin-right: 10mm;
}

/* round corners */
.c-mainCard,
.u-gapBottom--lg:first-child,
.u-gapBottom--sm,
.c-panel_content,
.c-dropdown__select ul,
#_popIn_img3_pc ._popIn_recommend_art_img {
	border-radius: 5px;
	box-shadow: 0 1px 1.5mm #888
}

.c-authorCard,
.l-wrap>.u-gapNextV--lg:nth-of-type(2),
.l-content__side .c-articleCard,
.c-group__options,
.c-dropdown--sort .c-dropdown__select ul,
.l-listTable,
.l-articlePage,
.l-searchCard,
.l-memberIntro,
.l-separationBg,
.l-wrap>div[itemprop="articleBody"] {
	border-radius: 2mm;
	box-shadow: 0 1px 2mm #666
}

.l-signInPage__content,
.l-content__main {
	border-radius: 3mm;
	box-shadow: 1px 2px 2mm #444
}

@media screen and (max-width: 1250px) {

	.t1,
	.t2 {
		font-size: 19px !important
	}

	.o-mainCardDesc {
		font-size: 16px
	}

	.c-mainCard__desc {
		padding: 3mm 5mm;
	}
}

@media screen and (min-width: 1251px) {
	body {
		overflow-x: hidden;
		scrollbar-width: none;
		-ms-overflow-style: none
	}

	.t1,
	.t2 {
		font-size: 1.5vw !important
	}

	.o-mainCardDesc {
		font-size: 1.3vw
	}

	.c-mainCard__desc {
		padding: .8vw 1.5vw
	}
}

@media screen and (min-width: 1600pt) {
	body {
		overflow-x: hidden;
		scrollbar-width: none;
		-ms-overflow-style: none
	}

	.t1,
	.t2 {
		font-size: 30px !important
	}

	.l-wrap {
		max-width: 1600pt
	}

	.l-content__main {
		max-width: calc(1600pt - 28mm)
	}
}

@media screen and (min-width: 1680pt) {
	.l-content__side:hover {
		left: calc(40vw - 1030pt);
	}
}
}
