[dir] #pv_more_acts_tt #pv_more_act_pe:before{background-position:0 -99px}
#pv_more_act_rotate_ccw{order:1}
#pv_more_act_rotate_cw{order:2}
#pv_more_act_spe{order:3}
#pv_more_act_pe{order:4}
#pv_more_act_move_to{order:5}
#pv_more_act_download{order:6}
#pv_more_act_to_profile{order:7}
#pv_more_act_place{order:8}
#pv_more_act_market{order:9}
#pv_more_act_as_title{order:10}
