/*  мод меню редактирования фоток - подсказки */
#pv_more_act_rotate_ccw:after,#pv_more_act_rotate_cw:after,#pv_more_act_move_to:after,#pv_more_act_pe:after,#pv_more_act_spe:after,#pv_more_act_place:after,#pv_more_act_to_profile:after,#pv_more_act_as_title:after,#pv_more_act_download:after,#pv_more_act_spam:after,#pv_more_act_to_dialog:after,#pv_more_act_market:after{color:#888;display:none;background:var(--color3);border:1px solid #222;padding:3px;margin:-46px -5px}
#pv_more_act_rotate_ccw:hover:after,#pv_more_act_rotate_cw:hover:after,#pv_more_act_move_to:hover:after,#pv_more_act_pe:hover:after,#pv_more_act_spe:hover:after,#pv_more_act_place:hover:after,#pv_more_act_to_profile:hover:after,#pv_more_act_as_title:hover:after,#pv_more_act_download:hover:after,#pv_more_act_spam:hover:after,#pv_more_act_to_dialog:hover:after,#pv_more_act_market:hover:after{display:block;position:absolute}
    
#pv_more_act_rotate_ccw:after{content:"поворот влево"}/*ROTATE LEFT*/
#pv_more_act_rotate_cw:after{content:"поворот вправо"}/*ROTATE RIGHT*/
#pv_more_act_move_to:after{content:"в альбом"}/*MOVE TO ALBUM*/
#pv_more_act_pe:after{content:"редактор"}/*PICTURE EDITOR*/
#pv_more_act_spe:after{content:"эффекты"}/*EFFECTS*/
#pv_more_act_place:after{content:"указать место"}/*MARK PLACE*/
