#pv_more_act_as_title:after{content:"обложка альбома"}/*SET AS ALBUM COVER*/
#pv_more_act_download:after{content:"открыть оригинал"}/*OPEN ORIGINAL*/
#pv_more_act_spam:after{content:"пожаловаться"}/*SPAM*/
