form[method="get"] tr:nth-of-type(3) td[colspan="2"] {
    display: inline-block;
    color: transparent !important;
    position: relative; left: 260px;
    background: rgba(var(--colorLightBlue)); border-radius: 5px;
    box-shadow: 0 0 4px rgba(var(--colorBlue),0.75);
    padding: 20px; margin: 10px 0 5px; }
form[method="get"] tr:nth-of-type(3) td[colspan="2"] > div {margin-top: -17px !important;}

input[type="text"][name="q"] {width: 98%;}
input[type="text"][name="u"] {width: 56%; margin-right: 10px;}

form[method="get"] td[width="300"] {width: 40% !important; text-align: left;}
form[method="get"] td[width="300"] + td {width: 40% !important; text-align: right;}

form[method="get"] select[name="uloc"] {height: 27px !important;}

.breadcrumb, .clearfix.mt4 {display: none !important;}
}

@-moz-document url-prefix("https://myanimelist.net/forum/") {
/* Bolded forum name / Title viewed from in thread */
.forum_locheader {
    position: absolute !important; top: 10px;
    width: 100% !important; max-width: 98% !important;
    white-space: nowrap; overflow: hidden; text-overflow: ellipsis;}

.breadcrumb > .di-ib:first-of-type > a > span {display: inline-block;text-indent: -9999px;width: 0;} /* 'Top' */

.borderClass.pl0.pr0.pt16.pb0.cl-b {padding-top: 0 !important;} /* Top of forums */

.clearfix.mt4 {height: 36px !important;} /* Top area */

div.fl-l > .breadcrumb {position: relative;top: 14px !important;padding-bottom: 0 !important;}
.forum_locheader + br + span {display: none !important;} /* News forum */
.pt4, .pt8 {padding: 0 !important;}

/* Post New Reply, Create New Topic, Ignore Board (Buttons), Post 'Top' */
