.fl-l.pb8 {padding: 4px 0 23px 7px !important;}
#iBoardId a[onclick*="ignoreBoard"] {margin-left: 5px !important;}
a#showQuickReply + a[href="#top"] {margin-left: -2px !important;}

/* Hide second breadcrumb (episode posts) */
