    font-size: 13px; font-weight: bold;
    font-style: normal !important;
    border: none !important; }
.header-right.divider:after {content: " + ";}

/* Remove quick reply cancel button */
#quickReply input#clearQuickReply {display: none !important;}

.breadcrumb {display: block !important;}
}

