/* Step functions */
.awsui-table-row a,
.sfn-dictionary-item {
    -webkit-filter: invert(100%) !important;
}
.sfn-dictionary-item {
    color: #777
}
.sfn-dictionary-item awsui-icon[name="status-positive"] {
    -webkit-filter: invert(100%) !important;
}
.sfn-dictionary-item span[translate="SUCCEEDED"] {
    -webkit-filter: invert(100%) !important;
}
.sfn-dictionary-item awsui-icon[name="status-negative"] {
    -webkit-filter: invert(100%) !important;
}
.sfn-dictionary-item span[translate="FAILED"] {
    -webkit-filter: invert(100%) !important;
}
.sfn-execution-status[status="$item.status"] awsui-icon[name="status-positive"] {
    -webkit-filter: invert(100%) !important;
}
span[class="awsui-util-status-positive"] {
    -webkit-filter: invert(100%) !important;
}
tr[class="awsui-table-row ng-scope"] span[class="awsui-icon awsui-icon-size-normal awsui-icon-variant-link"] {
    -webkit-filter: invert(100%) !important;
}

/* General UI elements */ 
.awsui-table-row awsui-radio-button {
    -webkit-filter: invert(100%) !important;
}
.awsui-radio-button-styled-circle  {
    fill: #44444440 !important;
    stroke: #444 !important;
}
.m-nav-mobile,
.image {
    -webkit-filter: invert(100%) !important;
}

/* Cloudwatch */
.GIYU-ANBKEF,
.GIYU-ANBLFF,
.cwdb-copilot-breadcrumb-current span {
    -webkit-filter: invert(100%) !important;
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.75);
}

.btn, .btn:visited {
    text-shadow: 0 1px 1px rgba(255, 255, 255, 0);
}

.btn.disabled, .btn:disabled, .btn[disabled] {
    background-image: linear-gradient(to bottom, #505050, #232323);
}

/* RDS */
.GK15FVNCDRF a {
color:white;
}

.GK15FVNCDRF a:visited {
color:darkgray;
}

.GK15FVNCDRF a:hover {
color:#6a686a;
}

.datapoint-leaderboard-row-color {
    -webkit-filter: invert(100%) !important;
}

/* Docs */
.awsdocs-banner,
.awsdocs-local-nav-header,
.awsdocs-header {
    -webkit-filter: invert(100%) !important;   
}

.awsdocs-header {
    background: #2b2b2b;
}

/* Notices */
.awsui .awsui-alert-type-info {
    border-color: #ff8c44bf;
}

/* Cloudwatch Charts*/

.cwdb-chart svg {
    background-color: #000;
}

.cwdb-status-insufficient .cwdb-chart-container-background {
    border: 2px solid #e07700b8;
}

.header-background {
    fill: #000;
}

.axis.dimmable .tick text {
    fill: #9d9d9d;
}   

.legend text {
    fill: #9d9d9d;
}

body .btn, body .btn:hover, body .btn:visited, body .Select-control, body .awsui .awsui-button.awsui-button-variant-primary, body .awsui .awsui-button.awsui-button-variant-primary:hover {
    background: #000;
    color: #ABA49B;
    border-color: #ABA49B;
    border-radius: 2px;
    cursor: pointer;
    text-decoration: none;
}

.cwui .cwui-datepicker .cwui-datepicker-toolbar .cwui-datepicker-quickpick-selected a {
    color: #EE9944;
    font-weight: bold;
}

.cwdb-page a {
    color: #EE9944;
    text-decoration: none;
}

.alarm-text-background {
    fill: #000000;
    opacity: 0.8;
}

/* SQS */

