data.css:
@-moz-document domain("chip.pl") {
body, #main {
	background: #111;
	color: #ccc;
}
.content {
	color: #bbb;
}
#main-header {
	background: #2b0809 url(data:image/jpeg;base64,/9j/4AAQSkZJRgABAQEASABIAAD/2wBDAAYEBAUEBAYFBQUGBgYH…tT7dsSKS0Rl1rAZD2EZvpQ3KckjTMtysBgaopXKWupY49sFZhAgICAgJgez9O/Dw+/ywj/2Q==) right no-repeat;
}
#main-header .menu a:focus, #main-header .menu a:hover, #main-header .current-menu-item>a,
#main-header .menu-button:focus, #main-header .menu-button:hover {
	background: #531012;
	color: #fff;
}
#main-header .sub-menu {
	background: #350a0b;
}
#main-header .menu>li, #main-header .search-toggle {
	border-right: .9px solid #401717;
}
#main-wrapper {
	padding-top: 60px;
}
a {
	color: #bbbbbb;
}
a:focus, a:hover {
	color: #f56d71;
}
.timeline .subtitle:not(:focus):not(:hover), .timeline .arrow:not(:focus):not(:hover), .timeline .more:not(:focus):not(:hover),
.listing .excerpt {
	color: #a2a2a2;
}
.timeline {
	border-left: 40px solid #111;
}
.tax-widget.ranking.featured .term {
	border-bottom: .9px solid #252525;
}
.widget-title a, .timeline .time, .widget-title, .widgettitle {
	background: #581315;
	color: #fff;
}
.post-widget .comments {
	background: rgba(80, 0, 3, 0.8);
	color: #fff;
}
.post-widget.plain-light-back .back {
	background: #0a0a0a;
}
.post-widget.dark-text .title {
	color: #fff;
}
.post-widget.dark-text .subtitle {
	color: #ccc;
}
.post-widget.dark-text .author,
.listing .date a:not(:focus):not(:hover) {
	color: #888;
}
.post-widget.light-back .back,
.post-widget.dark-back .back,
.post-widget.dark-back.image-right .back {
	background: #0a0a0a;
}
.splitter-widget {
	border: 1px solid #282828;
	background: #2f2f2f;
	color: #bdbdbd;
}
.single .meta {
	background: #222;
}
.sidebar:before {
	border-left: .9px solid #202020;
}
.content .wp-caption-text {
	color: #999;
}
.wp-embed {
	color: #bfc7ce;
	background: #111;
	border: 1px solid #202020;
}
.content iframe.wp-embedded-content {
	border: 1px solid #202020;
}
.wp-embed-heading a {
	color: #aebdcd;
}
.terms-widget a {
	border: 1px #581315 solid;
	background: #1a1010;
}
.terms-widget a:hover, .terms-widget a:focus {
	background: #581315;
	color: #fff;
}
.marks span, .marks button {
	border: 1px solid #581315;
}
.marks button {
	background: #581315;
}
.marks button:focus, .marks button:hover {
	border-color: #811313;
}
.category-label {
	background: #581315;
	color: #fff;
}
.pagination .current {
	background: #600003;
	color: #fff;
}
.pagination>a, .pagination>span {
	background: #262626;
	color: #aaa;
}
.pagination a:focus, .pagination a:hover {
	background: #841518;
	color: #fff;
}
.appear-media img.appear {
	opacity: 0.5;
}
[id=main-wrapper] a:focus img, [id=main-wrapper] a:focus video, [id=main-wrapper] a:hover img, [id=main-wrapper] a:hover video {
	opacity: 1;
}
}

@-moz-document url-prefix("https://disqus.com/embed/comments/?base=default&f=chippl") {
body {
	color: #d2d8d8;
}
.tab-conversation.active>a {
	color: #d2d8d8!important;
}
.nav-primary {
	border-bottom: 2px solid #171718;
}
.vote-down {
	border-left: 2px solid #181819;
}
.post-content .post-menu .dropdown-toggle {
	border-color: #181819;
}
.disqus-footer__wrapper {
	border-top: 2px solid #181819;
}
.caret {
	border-top: 4px solid #fff;
}
.discovery-col-header h2,
.discovery-post .meta,
.discovery-post .top-comment .user {
	color: rgba(92, 158, 197, 0.8);
}
.discovery-col-header h2 strong {
	color: #d3bebe;
}
.discovery-post .top-comment p {
	color: #9ca6ac;
}
.publisher-anchor-color a,
.comment__footer .author, .comment__header .author {
	color: #eee !important;
}
}

/* ==UserStyle==
@name Nyaa DeeperDark
@description Combination of "DeepDark" by RaitaroH and "Nyaa.si Dark" by Coordinates
@namespace github.com/GrimmDeth/Nyaa-DeeperDark
@homepageURL https://github.com/GrimmDeth/Nyaa-DeeperDark
@version 0.9.1
@updateURL https://raw.githubusercontent.com/GrimmDeth/Nyaa-DeeperDark/master/NyaaDeeperDark.user.css
@author GrimmDeth
@license GNU-V3.0

@preprocessor stylus

@var select stylus-deepdark-style "Preset themes" [
	"Deep-Dark",
	"Inspired-Dark",
	"Breeze-Dark",
	"HavocOS",
	"Arc-Dark",
	"Adapta-Nokto",
	"Adapta-Breath-Nokto",
	"Gruvbox-Dark",
	"Gruvbox-Light",
	"NierAutomata-Dark",
	"NierAutomata-Light",
	"Solarized-Dark",
	"Solarized-Light",
	"Vertex-Dark",
	"Mint-Y-Dark",
	"9anime",
	"Firefox-Dark",
	"Firefox-57",
	"Discord",
	"YouTube-Dark",
	"Black-and-White",
	"Yellow",
	"Yellow-2",
	"Ubuntu-Grey",
	"Ubuntu-Purple",
	"Orange",
	"Jisho"
]

==/UserStyle== */

@-moz-document domain("nyaa.si") {

/*GNU General Public License v3.0*/

	/*Main color variables*/
	:root {
	--main-color: #00adee;
        --main-background: #111;
        --second-background: #181818;
        --hover-background: #232323;
        --main-text: #eff0f1;
        --dimmer-text: #ccc;
        --editor-background: #181818;
        --select-background: #232323;
        --shadow: 0 1px 0.5px rgba(0, 0, 0, .3);
        --even-entry-background: rgba(0, 0, 0, .6);	
	}

	/*Body*/
    body {
        background: var(--second-background) !important;
        color: var(--main-text) !important;
    }

    /*Header*/
    .navbar {
		background: var(--main-background) !important;
		border: none !important;
		border-top: 4px solid var(--main-color) !important;
		box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.2);
    }
    .navbar-brand, .navbar-nav > li > a {
        color: var(--main-text) !important;
    }
    .navbar-brand:hover, .navbar-nav > li > a:hover {
        color: var(--main-color) !important;
    }
    .navbar-inverse .navbar-nav > .active > a, .navbar-inverse .navbar-nav > .active > a:hover, .navbar-inverse .navbar-nav > .active > a:focus {
        background: var(--main-background) !important;
	}
	.navbar-inverse .navbar-nav>.open>a, .navbar-inverse .navbar-nav>.open>a:hover, .navbar-inverse .navbar-nav>.open>a:focus {
		background-color: var(--main-background) !important;
	}
	/* HEADER: Dropdown menus */
	.navbar-nav>li>.dropdown-menu, .navbar-right .dropdown-menu {
		background: var(--select-background) !important;
		margin-top: 22px;
		border-radius: 6px;
		border: none;
		overflow: visible !important;
		padding: 3px;
		left: 0;
		right: auto;
	}
	.navbar-nav>li>.dropdown-menu {
		margin-top: 12px;
	}
	.dropdown-menu::before {
		content: '';
		width: 0;
		height: 0;
		border: 8px solid transparent;
		position: absolute;
		top: -16px;
		left: 25px;
		border-bottom: 8px solid #343b41;
	}
	.navbar-nav>li>.dropdown-menu a, .dropdown-menu li a {
		padding: 6px 10px;
		border-radius: 6px;
		margin: 3px;
		color: var(--main-color) !important;
	}
	.dropdown-menu li a[href="/logout"] {
		background: #ee5050 !important;
		color: var(--main-text) !important;
	}

	.dropdown-menu li a[href="/logout"]:hover {
		background: #c83232 !important;
		color: var(--main-text) !important;
	}
	.dropdown-menu li a:hover {
		background: var(--second-background) !important;
		color: var(--main-color) !important;
	}
	.dropdown-menu li a:focus {
		background: rgba(215, 235, 255, 0.1);
		color: var(--main-text) !important;
		outline: 0;
	}
	.dropdown-menu::-webkit-scrollbar {
		width: 0px;
	}
	.dropdown-menu>.active>a, .dropdown-menu>.active>a:hover, .dropdown-menu>.active>a:focus {
		background: rgba(215, 235, 255, 0.1);
		color: var(--main-color) !important;
	}
	/* HEADER: Search and filters */
	.input-group-btn:first-child>.btn, .input-group-btn:first-child>.btn-group, .btn-group {
		margin-right: 4px;
	}
	.btn-group.open .dropdown-toggle {
		box-shadow: none;
	}
	.bootstrap-select {
		border: none !important;
	}
	.bootstrap-select button {
		border: 1px solid rgba(0, 0, 0, 0.1) !important;
	}
	.navbar-form .dropdown-toggle {
		background: var(--select-background) !important;
		border: none;
		border-radius: 4px !important;
		color: var(--main-color) !important;
	}
	.dropdown-toggle.btn-default:hover, .dropdown-toggle.btn-default:focus, .dropdown-toggle.btn-default:active, .dropdown-toggle.btn-default:active:hover {
		background: var(--second-background) !important;
		color: var(--main-color) !important;
	}
	.bootstrap-select .dropdown-toggle:focus {
		outline: 0 !important;
	}
	.open>.dropdown-toggle.btn-default, .open>.dropdown-toggle.btn-default:focus, .btn-default:focus, .btn-default.focus {
		background: var(--second-background) !important;
		color: var(--main-color) !important;
	}
	.open>.dropdown-toggle.btn-default:hover {
		background: var(--second-background) !important;
		color: var(--main-color) !important;
	}
	.form-control.search-bar {
		background: var(--select-background) !important;
		border: 1px solid rgba(0, 0, 0, 0.1);
		border-radius: 4px !important;
		color: var(--main-text) !important;
		padding: 6px 12px;
		height: auto;
		margin-right: 3px;
	}
	input:-webkit-autofill,
	input:-webkit-autofill:hover,
	input:-webkit-autofill:focus,
	textarea:-webkit-autofill,
	textarea:-webkit-autofill:hover,
	textarea:-webkit-autofill:focus,
	select:-webkit-autofill,
	select:-webkit-autofill:hover,
	select:-webkit-autofill:focus {
		border: 1px solid rgba(0, 0, 0, 0.1);
		-webkit-text-fill-color: var(--main-text) !important;
		-webkit-box-shadow: 0 0 0px 1000px var(--select-background) inset !important;
		transition: background-color 5000s ease-in-out 0s;
	}
	.form-control.search-bar:focus {
		background: rgba(0, 0, 0, 0.35);
		box-shadow: none;
	}
	.input-group-btn:last-child>.btn {
		background: var(--main-color) !important;
		margin-left: 5px;
		border-radius: 4px;
	}
	.btn-primary, .btn-success, .btn-default {
		border: 0px solid rgba(0, 0, 0, 0.5) !important;
		color: var(--main-text) !important;
		background: rgba(215, 235, 255, 0.2);
		outline: 0 !important;
	}
	.btn-primary:focus, .btn-success:focus, .btn-default:focus {
		background: rgba(215, 235, 255, 0.2);
	}
	.btn-primary:hover, .btn-success:hover, .btn-default:hover {
		color: var(--main-text) !important;
		background: rgba(215, 235, 255, 0.30);
	}
	.btn-primary:active, .btn-success:active, .btn-default:active {
		color: var(--main-text) !important;
		background: rgba(215, 235, 255, 0.30) !important;
		box-shadow: none;
	}
	.btn-primary:active:hover, .btn-success:active:hover, .btn-default:active:hover {
		color: var(--main-text) !important;
		box-shadow: none !important;
	}
	.dropdown-menu .divider {
		margin: 0px 11px;
		background: rgba(0, 0, 0, 0.3);
	}
	.glyphicon {
		top: 10%;
	}
	.alert-info {
		background: rgba(117, 185, 255, .1) !important;
		border: 1px solid rgba(117, 185, 255, .1) !important;
		color: #FFF !important;
	}

	.table, .table-bordered>thead>tr>th, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>tbody>tr>td, .table-bordered>tfoot>tr>td {
		border: none;
	}
	.table {
		margin-bottom: 0px;
		overflow: hidden;
		background: rgba(0, 0, 0, 0.2);
	}
	.table-responsive {
		border-radius: 5px;
		border: 0px solid rgba(0, 0, 0, 0.4);
	}
	.table thead {
		background: var(--main-background) !important;
		color: var(--main-text) !important;
		border-bottom: 2px solid rgba(0, 0, 0, 0.4);
	}
	/* BODY: Torrents */
	.table tbody tr {
		border-top: 1px solid rgba(0, 0, 0, 0.4);
		padding: 5px;
	}
	.table tbody tr * {
		background: none !important;
		text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.7);
	}
	.table-hover>tbody>tr:hover {
		background: none;
	}
	.table-striped>tbody>tr.default {
		background: var(--second-background) !important;
	}
	.table-striped>tbody>tr:nth-of-type(odd) {
		background: var(--hover-background) !important;
	}
	.table-striped>tbody>tr.success, .panel-success .panel-heading {
		background: rgba(106, 213, 110, 0.2) !important;
	}
	.table-striped>tbody>tr.danger, .panel-danger .panel-heading {
		background: rgba(128, 53, 56, 0.5) !important;
	}
	/* BODY: Inset stuff */
	table.torrent-list tbody tr td a, table.torrent-list tbody tr td a:visited, .pagination-page-info {
		color: var(--main-text) !important;
	}
	table.torrent-list tbody tr td a:hover, .pagination-page-info {
		color: var(--main-color) !important;
	}
	.table tbody tr a.comments {
		background: rgba(0, 0, 0, 0.5) !important;
		color: #FFF;
		border: none;
		margin-top: 1px;
	}
	.hdr-link {
		width: 56px !important;
	}
	.text-center {
		font-weight: 600;
	}
	tbody .text-center {
		text-align: right;
	}
	td:nth-child(6) {
		color: #008000 !important;
	}
	td:nth-child(7) {
		color: #F35 !important;
	}
	.fa-download:hover {
		color: #008000 !important;
	}
	.fa-magnet:hover {
		color: #F35 !important;
	}
	/* BODY: Torrent Pages */
	.panel {
		background: rgba(0, 0, 0, 0.2);
		text-shadow: 0px 1px 1px rgba(0, 0, 0, 0.5);
		border: none;
		box-shadow: 0px 1px 5px rgba(0, 0, 0, 0.25);
	}
	.panel-heading {
		border: none;
		box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2);
	}
	.panel-default .panel-heading {
		border: none;
		background: rgba(215, 235, 255, 0.15);
		color: #FFF;
	}
	.panel-success .panel-heading {
		color: #CEC;
	}
	.panel-danger .panel-heading {
		color: #FFF;
	}
	.panel-body {
		color: #FFF;
	}
	.panel-body a {
		color: var(--main-color) !important;
	}
	.panel-footer {
		background: rgba(215, 235, 255, 0.10);
		color: #FFF;
		border: none;
	}
	.panel-footer a {
		color: var(--main-color) !important;
	}
	.panel-footer .fa-download {
		color: #008000 !important;
	}
	.panel-footer .fa-magnet {
		color: #F35 !important;
	}
	#comments {
		padding-bottom: 10px;
	}
	.comment-box {
		margin-top: 20px;
	}
	#comments .panel-title {
		color: var(--main-text) !important;
	}
	#comment, body>.container input:not(.btn), textarea, select.form-control {
		background: var(--second-background) !important;
		border: 1px solid rgba(215, 235, 255, 0.1) !important;
		color: #FFF !important;
	}
	#comment:focus, body>.container input:not(.btn):focus, textarea:focus, select.form-control:focus {
		outline: 0 !important;
		color: #FFF;
		border: 1px solid rgba(215, 235, 255, 0.1) !important;
		box-shadow: none !important;
		background: var(--hover-background) !important;
	}
	#comments .btn-sm {
		background: var(--main-color) !important;
		float: right;
		padding: 10px 20px;
		color: var(--main-text) !important;
	}
	.alert-warning {
		background: #f3e1833b;
		border-color: #f3e1833b;
		color: #f3e183;
		margin: 5px;
	}
	/*PAGINATION*/
	.pagination li a {
		margin: 5px;
		margin-top: 0px;
		border-radius: 3px;
		background: var(--main-background) !important;
		color: var(--main-text) !important;
		border: none;
	}
	.pagination li a:focus {
		background: var(--main-background) !important;
		color: var(--main-text) !important;
	}
	.pagination li a:hover {
		background: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.pagination li.active a {
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	.pagination li.active a:hover {
		background: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.pagination .active a:focus {
		background: var(--hover-background) !important;
	}
	.pagination li.disabled a, .pagination li.disabled a:hover, .pagination li.disabled a:focus {
		background: var(--main-background) !important;
	}
	.pagination li:first-child a, .pagination li:last-child a {
		padding: 2px 8px;
		margin-top: 4px;
	}
	/* RESPONSIVE FIXES */
	@media (max-width: 1228px) {
		.navbar-nav>li>.dropdown-menu, .navbar-right .dropdown-menu {
			left: auto;
			right: 0;
		}
		.dropdown-menu::before {
			right: 25px;
			left: auto;
		}
	}
	footer > p:nth-child(1) {
		display: none !important;
	}
	footer a {
		color: var(--main-color) !important;
	}
}

@-moz-document regexp(".*nyaa.si/profile") {
	ul.nav-tabs#profileTabs {
		margin: 0 !important;
		border: none !important;
	}
	.nav-tabs > li {
		margin: 0 !important;
	}
	#profileTabs li a {

		color: var(--main-text) !important;
		border: none !important;
	}
	.nav > li > a:focus {
		outline: 0;
		background: var(--main-background) !important;
	}
	#profileTabs li:not(.active) {
		background: var(--select-background) !important;
		border-radius: 2px 2px 0px 0px !important;
		outline: 0 !important;
	}
	#profileTabs li:not(.active) a:hover {
		background: rgba(215, 235, 255, 0.05) !important;
	}
	#profileTabs .active a {
		background: var(--main-background) !important;
		color: var(--main-color) !important;
	}
	.tab-content {
		background: var(--main-background) !important;
		padding: 15px !important;
		margin-bottom: 20px !important;
		border-radius: 6px !important;
		border-top-left-radius: 0px !important;
	}
	.btn-primary {
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	hr {
		display: none !important;
	}
}

@-moz-document regexp(".*nyaa.si/help"), regexp(".*nyaa.si/rules"), regexp(".*nyaa.si/profile"), regexp(".*nyaa.si/upload") {
	body > .container {
		background: var(--main-background) !important;
		border-radius: 10px;
		margin-bottom: 20px;
	}
	body a {
		color: var(--main-color) !important;
	}
}

@-moz-document regexp(".*nyaa.si/upload") {
	.input-group .btn-default {
		background: var(--main-color);
	}
	ul.nav-tabs {
		margin: 0;
		border: none;
	}
	.nav-tabs > li {
		margin: 0;
	}
	.nav-tabs li a {
		color: var(--main-text) !important;
		border: none;
	}
	.nav > li > a:focus {
		outline: 0;
		background: none;
	}
	.nav-tabs li:not(.active) a:hover {
		background: rgba(215, 235, 255, 0.05);
	}
	.nav-tabs > li.active > a {
		background: rgba(215, 235, 255, 0.1) !important;
		border: none !important;
	}
	.tab-content {
		background: rgba(215, 235, 255, 0.1);
		padding: 15px;
		margin-bottom: 20px;
		border-radius: 6px;
		border-top-left-radius: 0px;
	}
	.well {
		margin: 0;
	}
	form .row:last-child {
		margin-top: -20px;
	}
	#description-markdown-target {
		background: var(--second-background) !important;
		border: none;
	}
	.btn {
		border: none !important;
	}
	.btn-default:active, .btn-default.active {
		background-color: rgba(215, 235, 255, 0.2) !important;
	}
	.btn-default:active:hover, .btn-default.active:hover {
		background-color: rgba(215, 235, 255, 0.3) !important;
	}
}

