@-moz-document domain("www.cherp.chat") {
/* SITE BACKGROUND */

body {
    background: #000000 !important;
    color: #eee !important;
}

/* CONTINUING PROMPT GRADIENT FADE */

.fade {
    display: block;
    position: absolute;
    top: 140px;
    left: 0;
    right: 0;
    height: 50px;
    background-image: linear-gradient(to bottom, rgba(0, 0, 0, 0), rgb(97, 97, 97));
}
/* CATEGORIES TEXT */

.subtitle, .help, label, #prompt_dropdowns {
    font-size: 10pt;
    color: #fff;
}
/* CATEGORY SELECTOR */

.tile2 {
    position: relative;
    -webkit-column-break-inside: avoid;
    z-index: 2;
    box-sizing: border-box;
    max-width: 650px;
    margin: 20px auto;
    padding: 20px 25px;
    background-color: #9b8f8f;
    box-shadow: 0 1px 2px #000;
}
/* CATEGORIES */
#categories, #categories textarea {
    background-color: #000000;
}
/* PROMPT SELECTION OVERLAY */

.show_overlay #overlay,
.show_report_overlay #report_overlay {
    display: block;
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 4;
    overflow-y: scroll;
    padding: 0 30px;
    background-color: rgba(0, 0, 0, 0.75);
}
/* SITE HEADER */

header {
    background-color: #000;
    color: #fff;
}
/* CHATS PAGE INDICATOR */

.tile2.pager {
    z-index: 1;
    margin: 0;
    padding-top: 10px;
    padding-bottom: 10px;
    background-color: #2C2C2C;
    box-shadow: 0 1px 2px #000;
    text-align: center;
}

/* "YOUR PROMPTS" CATEGORY NOT SET TEXT */
.danger h1, .danger h2, .danger h3, .danger h4, .danger h5, .danger h6, .danger a, .error {
    color: #a80000;
}

/* TITLES */
a {
    color: #666;
}

/* PROMPTS */
.tile2 {
    position: relative;
    -webkit-column-break-inside: avoid;
    z-index: 2;
    box-sizing: border-box;
    max-width: 650px;
