/* ==UserStyle==
@name           wikipedia.rehash
@namespace      krasjet
@homepageURL    https://github.com/Krasjet/wikipedia.rehash
@version        1.0.0
@description    A minimal typographic rehash of Wikipedia aimed at better screen readability.
@license        CC-BY-NC-4.0
@author         Krasjet
==/UserStyle== */
@-moz-document domain("wikipedia.org"),
domain("wikibooks.org"),
domain("mediawiki.org"),
domain("wiktionary.org") {
  /* narrower body */
  body {
    max-width: 68rem;
    margin-left: auto;
    margin-right: auto;
    position: relative;
    float: none;
    background-color: #fdfdfd;
  }

  /* remove the blue border on the left */
  .mw-body {
    border-left: none;
  }

  /* navbar */
  #mw-panel .portal {
    margin: 0 0.7rem 0 0.5rem;
    font-size: 1.1rem !important;
  }

  /* fix search box */
  .suggestions {
    right: 16px !important;
  }

  .selector-left {
    left: 120px !important;
  }

  /*
   * larger font size and more space between lines
   * charter can be downloaded here: https://practicaltypography.com/fonts/charter.zip
   */
  .mw-body-content {
    font-size: 1.3rem !important;
    line-height: 1.65;
    font-family: charter, serif;
  }

  #mw-content-text h2 {
    margin-top: 1.15rem;
    font-size: 120% !important;
  }

  /* slightly lower contrast */
  .mw-body-content p {
    color: #222222;
  }

  #content {
    background-color: #fcfcfc;
  }

  /* remove the gradient of the banner */
  #mw-page-base {
    background-color: #fdfdfd;
    background-image: none !important;
  }

  /* prevent math image overflow */
  dd .mwe-math-fallback-image-inline {
    max-width: 100% !important;
  }

  /* fix the equation box issue */
  .equation-box dd .mwe-math-fallback-image-inline {
    max-width: none !important;
  }

  /* slightly smaller font size for various elements */
  /*
   * I'm using Fira Mono as my monospace font.
   * You might have to tweak this part for your setup.
   */
  pre,
  code,
  .monospaced {
    font-size: 75% !important;
  }

  .hatnote {
    font-size: 90% !important;
  }

  /* list items */
  #mw-content-text ul,
  #mw-content-text ol {
    font-size: 96% !important;
  }

  table {
    font-size: 85% !important;
  }

  table pre,
  table code,
  table .monospaced {
    font-size: 85% !important;
  }

  #toc {
    font-size: 90% !important;
  }

  #toc h2 {
    font-size: 100% !important;
  }

  #mp-topbanner {
    font-size: 80% !important;
  }

  .thumbcaption {
    font-size: 85% !important;
  }
}

