.df-img-skin-premium {
    opacity: 0.15;
    pointer-events: none !important;
}

/* Gør border farverne mørkere */
.topnav {
    border: 1px solid #4d4d4d;
}
.drfront .df-article {
    border-top: 1px solid #4d4d4d;
}

/* Ændrer bg farven i "Det mener Ekstra Bladet */
.eb-bg--graa5 {
    background: #262626;
}

/* Fjerner unødvendig border */
a.list--flex.eb-row--itemstretch.list-item.list-item--column.eb-row-item--grow.size1of3.eb-color--black {
    border: none;
}

/* Menu bg */
.menu--overlay {
    background-color: hsla(0, 0%, 0%, 0.97);
}

/* Fakta boks gradient */
.facts-coverup--graa6 {
    background: -webkit-gradient(linear,left top,left bottom,from(hsla(0, 0%, 0%, 0)),to(#000000));
    background: linear-gradient(hsla(0, 0%, 0%, 0),#000000);
}
}
