@-moz-document domain("kitsu.io") {
/* Major */
body {
	background:#151515 !important;
	color:#ddd;
}
hr {
	border-top:1px solid #444
}

.feed-container {
	width:1200px;
}

a:focus {
	text-decoration:none;
}

/* Top Bar */
.navbar {
	background:#0c0c0c;
	border-color:#1f1f1f;;
}
.cover-page .transparent .navbar-buttons input,.site-search__input {
	background:#222;
	border-color:#252525;
	color:#bbb;
}

.cover-page .transparent .navbar-buttons input:hover,.site-search__input:hover {
	background:#252525;
	border-color:#282828;
	color:#ddd;
}

.cover-page .transparent .navbar-buttons input:focus,.site-search__input:focus {
	background:#2b2b2b;
	border-color:#2d2d2d;
	color:#ccc;
}
/* Notification List */
.notifications svg path {
	fill:#ddd;
}
.notification-list .media {
	color:#ddd;
}
.notifications:hover svg path {
	fill:#fd755c;
}
.nav-item .notifications:before {
	background:#fd755c;
	transition:all 300ms ease-in-out;
	width:25px;
	position:absolute;
	height:25px;
	content:"";
	border-radius:100%;
	margin-top:-25%;
	margin-left:-25%;
	transform:scale(0.1);
}
.cover-page .transparent .navbar-buttons li svg path,.nav-item.open .notifications svg path,.site-search__icon .icon svg {
	fill:#efefef;
}
.site-search__input.active {
	background:#2d2d2d;
}
.nav-item.open .notifications:before {
	background:#fd755c;
	transform:scale(1.0); 
	border-color:#d34833;
	width:25px;
	height:25px;
	position:absolute;
	content:"";
	border-radius:100%;
}
.nav-item.open .notifications:hover:before {
	background:#ff826b;
	transform:scale(1.0);
	border-color:#d34833;
	width:25px;
	height:25px;
	position:absolute;
	content:"";
	border-radius:100%;
}
.nav-item .notifications:hover {
	background:#252525;
	border-color:#282828;
}	
.notification-drop {
	border-color:#333;
}
.nav-item .notifications {
	background:#222;
	border-color:#252525;
}
.notification-list .media {
	background:#1f1f1f;
	border-color:#191919;
}
.notification-drop header {
	border-color:#222;
}
.notification-list .media:hover {
	background:#151515;
	border-color:#191919;
}
.cover-page .transparent .navbar-buttons .nav-item.open .notification-drop a a,.open>.dropdown-menu a a{
	color:#e06552;
}
.cover-page .transparent .navbar-buttons .nav-item.open .notification-drop a a:hover,.open>.dropdown-menu a a:hover{
	color:#f3290a;
}
.notification-list .media.new {
	background:#4c251e;
}
.notification-list .media.new:hover {
	background:#6d3d34;
}
.notifications.notifications--present {
	background:linear-gradient(RGBA(237,72,95,1),RGBA(242,124,54,1));
	transition:all 100ms linear;
}
.notifications.notifications--present:hover {
	background:linear-gradient(RGBA(242,124,54,1),RGBA(237,72,95,1));
}

/* Quick Updater */

.quick-update .flickity-viewport .action-wrapper textarea {
	background:#222;
	border-bottom-color:#2b2b2b;
	color:#ddd;
}
.quick-update .flickity-viewport .quick-update-item .overlay-panel {
	background:#2d2d2d;
}

.quick-update {
	background:#1b1b1b;
	border-color:#1f1f1f;
}


/* Trending Page */
.explore-search .explore-search--wrapper:hover svg {
	fill:#ccc;
}
.trending-banner {
	background:#191919;
}
.following-activity .activity-item,.trending-activity--header {
	border-color:#151515;
	color:#ccc;
}
.trending-banner--header .trending-row--title {
	color:#ccc;
}

.participating-following .stream-like .like-avatar .lazy-image {
	border-color:#101010;
}
.trending-banner--activity {
	background:#222;
	border-color:#151515;
}
.following-activity .media-body {
	color:#bbb;
}
.trending-banner--activity:after {
	background:linear-gradient(to bottom,RGBA(11,11,11,0),RGBA(11,11,11,1));
}
.trending-row .nav .nav-link {
	background:#666;
	color:#151515;
	border-color:#676767;
}

.trending-row .nav .nav-link.active {
	background:#aaa;
	color:#151515;
	border-color:#acacac;
}

.trending-row .nav .nav-link.active:hover {
	background:#afafaf;
	border-color:#afafaf;
	color:#151515;
	
}

.trending-row .nav .nav-link:hover {
	background:#999;
	border-color:#9a9a9a;
	color:#151515;
	
}
.trending-row:nth-child(even) {
	background:#191919;
	border-color:#222
}
.trending-cell .review-author .lazy-image {
	border-color:#191919;
}
.trending-review-section .trending-review {
	border-color:#222;
	background:#222;
	min-height:300px;
}
.trending-review-section .review-header h4 a {
	color:#ccc;
}
.trending-cell .media-title {
	color:#ccc;
}
.trending-review-section .trending-review p {
	color:#aaa;
}


/* dropdown menus */
.dropdown-item svg {
	fill:#16a085;
}

.cover-page .transparent .primary-nav--drop a,.open>.dropdown-menu a{
	color:#aaa;
}
.cover-page .transparent .primary-nav--drop a:hover,.open>.dropdown-menu a:hover{
	color:#ccc;
}
.open>.dropdown-menu header a {
	color:#e06552;
}
.open>.dropdown-menu header a:hover {
	color:#f3290a;
}
a:not([href]):not([tabindex]), a:not([href]):not([tabindex]):focus, a:not([href]):not([tabindex]):hover {
	color:#aaa;
}
.dropdown-menu{
	background:#101010;
}
.dropdown-item:focus, .dropdown-item:hover, .dropdown-item:hover a{
	background:#252525;
	color:#ccc;
}
.open>.dropdown-menu a header a:hover{
	color:#f3290a;
}
.cover-page .transparent .navbar-buttons .nav-item.open .notification-drop header a {
	color:#d65743;
}
.dropdown-checkmark svg path{
	fill:#16a085;
}
.user-menu-drop .dropdown-item{
	border-color:#151515;
}
/* Feed page */
.feed-stream {
	width:600px;
}

/* left side bar */
.feed-aside .timeline-link,.feed-aside .panel-heading,.my-groups-widget .joined-group a {
	color:#888;
}
.my-groups-widget .joined-group a:hover {
	background:#2a2a2a;
}

.feed-aside .timeline-link.active {
	background:#202020;
	border:#151515;
	color:#aaa
}
.feed-aside .timeline-link.active:hover{
	background:#272727;
	border:#151515;
	color:#f3290a;
}
.feed-aside .timeline-link:hover{
	background:#2a2a2a;
	border:#222;
	color:#f3290a;
}

/* Scrollbar */
::-webkit-scrollbar {
	width:6px;
	height:6px;
}
::-webkit-scrollbar-button {
	display:none;
}
::-webkit-scrollbar-track {
	background-color:#111;
	width:5px;
}
::-webkit-scrollbar-track-piece {
	display:none;
}
::-webkit-scrollbar-thumb {
	background-color:#e06552;
}
::-webkit-scrollbar-corner {
	display:none;
}
::-webkit-resizer {
	display:none;
}
nav-item.open .notifications {
	background:#a8a3a9;
}

/* Right side bar */

.sidebar-footer .footer-links .site-link {
	color:#aaa;
}
.sidebar-footer .footer-links .site-link:hover {
	color:#bbb;
}
.panel-heading {
	border-color:#333;
}
.sidebar-footer .kitsu-social svg {
    fill:#afafaf;
}
/* Media Filter Button*/
.feed-actions button:focus, .feed-actions .feed-filter button {
	color:#bbb;
}

.feed-actions .dropdown.open button,.feed-actions button:hover {
	color:#e06552;
}

.hint-panel {
	background:#151515;
	color:#bbb;
}
.rc-progress-line-trail {
	stroke:#151515;
}
.stream-error-wrapper {
	background:#222;
	box-shadow:0 1px 0 5px #222;
}

.checklist h6, .step,.user-blotter h6 {
	border-color:#151515;
}
.user-blotter .dismiss-blotter
#auth-modal .modal-content,.empty-wrapper {
