	background:#333;
}
.user-blotter .dismiss-blotter:hover {
	background:#732c2c;
}
.user-blotter .dismiss-blotter:hover svg{
	fill:#c0392b;
}

.guest-search, .guest-search input {
	background:#1d1d1d;
	color:#bbb;
}
.community-btn {
	background:#1b1b1b;
}
.community-btn:hover {
	background:#222;
}

/* Search change */
.search--group-header {
	background:#222;
	border-color:#252525;
}
.search--header {
	background:#151515;
}
.search--results .media:hover {
