}
.stream-item-comments li{
	border-color:#222;
}
.stream-item-comments {
	background:#1d1d1d;
	border-color:#222;
}
.form-control {
	background:#1b1b1b;
	border-color:#222;
	color:#ddd;
}
.form-control:focus {
