}
.ember-power-select-search-input {
	background:#222;
	color:#aaa;
}
.button--primary, .notification-settings--headers .button {
	color:#151515;
}
/* Tags */
.tagged-media {
	background:#2d2d2d;
	border-color:#2d2d2d;
}
.tagged-media--wrapper .tagged-media {
	background:#151515;
	border-color:#151515;
}
.role-tag {
	color:#151515;
}

/* Cards */
.card {
	background:#222;
	color:#ccc;
}
.card-title {
	color:#ccc;
}
.user-card .card-img-top {
