@-moz-document domain("www.baeldung.com") {
  .container,
  body {
    background-color: rgb(45, 45, 45);
  }

  #content {
    background-color: rgb(45, 45, 45) !important;
  }

  #main article {
    padding: 0 40px;
  }

  #main article,
  .wpdiscuz-front-actions,
  #wpcomm .wc-comment .wc-comment-right,
  #wpcomm .wc-reply .wc-comment-right {
    background-color: rgb(45, 45, 45);
  }

  .header--menu {
    position: relative;
  }

  .author-by {
    color: rgb(231, 232, 235);
  }

  .static-category-title,
  a:link,
  a:visited {
    color: rgb(231, 232, 235);
  }

  /* comentarios */
  .single section.post_content h2,
  .single section.post_content h3,
  .single section.post_content p,
  .single section.post_content ul,
  .single p.post-modified,
  .single h1.single-title,
  .author-by,
  #wpcomm .wc-comment-text,
  .wpdiscuz-sort-buttons,
  .wpdiscuz-sort-button,
  .tve_flt .tve_cb6 .tve_cb_cnt p {
    color: rgb(231, 232, 235);
  }

  /* comentarios */
  #wrap .hljs-string,
  #wrap .hljs-variable,
  #wrap .hljs-template-variable,
  #wrap .hljs-symbol,
  #wrap .hljs-bullet,
  #wrap .hljs-section,
  #wrap .hljs-addition,
  #wrap .hljs-attribute,
  #wrap .hljs-link,
  #wrap .hljs-comment,
  #wrap .hljs-quote,
  #wrap .hljs-meta,
  #wrap .hljs-deletion,
  #wrap .hljs-tag {
    color: rgb(231, 232, 235);
  }

  .single .syntaxhighlighter,
  .syntaxhighlighter table td.code .line,
  .single .syntaxhighlighter .gutter .line,
  .hljs {
    background-color: rgb(61, 61, 61) !important;
  }

  .single .syntaxhighlighter .keyword {
    color: rgb(231, 232, 235) !important;
  }

  .syntaxhighlighter .gutter {
    color: #2f2828 !important;
  }

  .syntaxhighlighter {
    padding: 0px !important;
  }

  .syntaxhighlighter .color1 {
    color: #252924 !important;
  }

  .syntaxhighlighter .value {
    color: rgb(231, 146, 154) !important;
  }

  .single .syntaxhighlighter .string {
    color: rgb(231, 146, 154) !important;
  }

  .single .post-content p,
  .post-content strong,
  .post-content li,
  .post-content div {
    color: rgb(231, 232, 235);
  }

  .code-block,
  .further-reading-posts,
  .before-post-widgets,
  .meta-row,
  .adtester-container {
    display: none;
  }

  /* strings */
  body #wrap .syntaxhighlighter .keyword,
  body #wrap .syntaxhighlighter .string,
  body #wrap .syntaxhighlighter .string a {
    color: rgb(231, 146, 154) !important;
  }

  /* strings */
  #wrap .hljs-string,
  #wrap .hljs-variable,
  #wrap .hljs-template-variable,
  #wrap .hljs-symbol,
  #wrap .hljs-bullet,
  #wrap .hljs-section,
  #wrap .hljs-addition,
  #wrap .hljs-attribute,
  #wrap .hljs-link,
  #wrap .hljs-comment,
  #wrap .hljs-quote,
  #wrap .hljs-meta,
  #wrap .hljs-deletion,
  #wrap .hljs-tag {
    color: rgb(231, 146, 154) !important;
  }

  /* codigo normal */
  .syntaxhighlighter .plain,
  .syntaxhighlighter .plain a,
  .hljs-title,
  .hljs-section {
    color: rgb(218, 222, 224) !important;
  }

  /* codigo normal */
  .hljs-number,
  .hljs-built_in,
  .hljs-builtin-name,
  .hljs-literal,
  .hljs-type,
  .hljs-params,
  .hljs-meta,
  .hljs-link {
    color: rgb(218, 222, 224) !important;
  }

  /* codigo normal */
  td.hljs-ln-line.hljs-ln-code {
    color: rgb(218, 222, 224) !important;
  }

  .syntaxhighlighter .color1,
  #wrap .hljs-keyword,
  #wrap .hljs-selector-tag {
    color: rgb(151, 230, 254) !important;
  }

  .syntaxhighlighter .comments,
  .syntaxhighlighter .comments a {
    color: rgb(159, 166, 173) !important;
  }
  body .syntaxhighlighter,
  body .syntaxhighlighter a,
  body .syntaxhighlighter div,
  body .syntaxhighlighter code,
  body .syntaxhighlighter table,
  body .syntaxhighlighter table td,
  body .syntaxhighlighter table tr,
  body .syntaxhighlighter table tbody,
  body .syntaxhighlighter table thead,
  body .syntaxhighlighter table caption,
  body .syntaxhighlighter textarea {
    font-family: Consolas, Menlo, Monaco, "Lucida Console", "Liberation Mono",
      "DejaVu Sans Mono", "Bitstream Vera Sans Mono", "Courier New", monospace,
      sans-serif !important;
  }

  /* xml atriutos */
  span.hljs-attr {
    color: rgb(151, 230, 254) !important;
  }

  body {
    font-family: Arial, "Helvetica Neue", Helvetica, sans-serif !important;
    color: rgb(231, 232, 235) !important;
  }

  #wrap a:link,
  #wrap a:visited {
    color: rgb(60, 164, 255);
  }

  #wrap a {
    color: rgb(60, 164, 255);
  }

  .nav--menu_item a:link {
    color: rgb(159, 166, 173) !important;
  }

  .nav--menu_item a:visited {
    color: rgb(159, 166, 173) !important;
  }

  .container,
  body {
    background-color: rgb(57, 57, 57);
  }

  /* vinculo que aparece para elegir uno de los titulos */
  h2:hover .anchor-link svg,
  h3:hover .anchor-link svg,
  .anchor-link:hover svg {
    filter: invert(100%) sepia(100%) saturate(6878%) hue-rotate(3420deg)
      brightness(54%) contrast(97%);
  }

  /* titulo para comentarios */
  #wpcomm .wpdiscuz-front-actions {
    background: rgb(57, 57, 57);
  }

  #wpcomm .wc-comment .wc-comment-left .wc-comment-label {
    background: rgb(57, 57, 57);
  }

  #wpcomm .wc-comment-header {
    border-top: 1px solid rgb(57, 57, 57);
  }

  #wpcomm .wc-blog-guest > .wc-comment-left .wc-comment-label {
    border-bottom: 1px solid rgb(57, 57, 57);
  }

  #wpcomm .wpdiscuz-textarea-wrap {
    border: none;
  }

  /* footer */

  .ezmob-footer-desktop {
    background-color: black;
    border-top: none;
  }

  .widget {
    display: none;
  }
}
