@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*") {
/* Stylus LT800 (en-US) ver.2020.07.22 */
/* Interface for Installed and Edit Styles  ============== */
/* background for all */
body {
    background: var(--bgc0); }

/* menu - left side */
#header {
    background: var(--bgc1);
    border-right: 1px solid var(--bgc9);
    overflow-x: hidden; }

/* header menu title */
#header h1,
#header h2 {
    color: var(--bgc6); }

#header summary {
    color: var(--bgc6); }

/* header basic letter */
#header span,
#header label,
#faviconsHelp,
#header span a,
#lint table {
    color: var(--bgc6); }

/* svg-icon */
#header .svg-icon:not(.checked),
#sections .svg-icon {
    fill: var(--bgc6); }

/* button */
button {
    height: 24px;
    border: 1px solid;
    border-radius: 4px;
    outline: none;
    overflow: hidden; }

/* check button */
input[type="checkbox"]:not(.slider) {
    border-radius: 10px;
    transition: none; }

.svg-icon.checked {
    fill: transparent; }

.newUI .entry .svg-icon.checked,
.newUI .entry:hover .svg-icon.checked {
    fill: transparent; }

/* Installed Styles ======== Basic ================ */
/* filter */
#filters-stats {
    border-radius: 4px;
    background: var(--bgc8) !important; }

#filters .filter-selection {
    height: 16px;
    margin-bottom: 4px;
    border-radius: 4px;
    overflow: hidden; }

#filters label:hover,
#filters .filter-selection:hover {
    background: rgba(255, 255, 255, .3); }

#filters .filter-selection select {
    color: var(--bgc6); }

#filters .filter-selection option {
    background: var(--bgc7); }

#only-updates {
    padding: 1px 0 1px 24px !important;
    border-radius: 4px; }

#only-updates:hover {
    background-color: hsla(0, 0%, 50%, .2) !important; }

#only-updates input {
    margin: 0; }

/* filters match no styles */
body.all-styles-hidden-by-filters:before,
body.all-styles-hidden-by-filters:after {
    color: var(--bgc8); }

/* search and sort */
#search,
#manage\.newUI\.sort {
    border-radius: 4px;
    color: var(--bgc6);
    background: var(--bgc7); }

#manage\.newUI\.sort {
    width: 100% !important; }

#sort-wrapper {
    margin-bottom: 1.2em; }

/* update history */
#message-box > div {
    border-radius: 4px; }

#message-box-title {
    border-radius: 4px 4px 0 0; }

#message-box-buttons {
    border-radius: 0 0 4px 4px; }

/* Caution local edits */
#check-all-updates-force {
    background: linear-gradient(#f00 0%, #ac0000 100%);
    height: auto; }

/* applies-to items */
#newUIoptions input[type="number"] {
    border: 1px solid #aaa;
    border-radius: 4px;
    padding-left: 7px;
    outline: none;
    color: var(--bgc6);
    background: var(--bgc7); }

/* Backup buttons */
#backup-buttons .dropbtn span {
    color: #fff; }

#backup-buttons .svg-icon.select-arrow {
    fill: #fff; }

#backup-buttons .dropdown-content {
    min-width: unset;
    white-space: nowrap; }

/* Get styles */
#manage-text a {
    display: inline-block;
    height: 14px;
    padding: 0 8px;
    border-radius: 4px;
    text-decoration: none;
    transition: .25s; }

#manage-text a:hover {
    color: #000;
    background: rgba(255, 255, 255, .7); }

#manage-text > :not(:last-child):after {
    content: "";
    margin: 0; }

/* check button in installed list */
#installed .checkmate input[type="checkbox"]:not(.slider) {
    left: -4px;
    top: -2px;
    height: 15px;
    width: 15px; }

/* disable click on around style name */
.newUI .style-name {
    pointer-events: none; }

.newUI .style-name-link {
    text-decoration: none;
    pointer-events: auto; }

/* installed list -- letters and icons */
.enabled .style-name-link,
.enabled .actions,
.enabled .applies-to {
    color: var(--bgc6); }

.enabled .style-name:hover .style-name-link {
    color: var(--bgc6) !important;
    filter: brightness(100%); }

.disabled .style-name-link,
.disabled .actions,
.disabled .applies-to {
    color: var(--bgc6); }

.disabled .style-name:hover .style-name-link {
    color: var(--bgc6);
    opacity: 0.99; }

/* style-name unified in bold */
.disabled h2 .style-name-link {
    font-weight: bold; }

/* hide "disabled" */
.disabled h2::after {
    font-size: 0;
    padding: 0; }

/* expander button */
.expander {
    color: var(--bgc6); }

/* balance of width : style-name and applies-to */
.newUI .target {
    max-width: calc(32vw - var(--actions-width)); }

/* Installed Styles ======== Less than 850px ========= */
@media screen and (max-width: 850px) {
/* menu header -manage- */
#stylus-manage #header {
