/*Лог боев и панелька*/
#fightPanel {
    background-color: #2b2c33;
    border: 0.5px solid #2b2c33;
}

/*Кнопки на боепанели*/
.hotkey {
    background-color: #0059b3;
    color: #1c1c1c;
    border: 0.5px solid #0059b3;
    border-radius: 5px;
}
.hotkey:active {
    border-radius: 5px;
}
div#cages_div {
    opacity: 1 !important;
}

/*Убрать выделение с кнопок*/
:focus {
    outline-style: none;
    outline-width: 0px !important;
}

/*Цвет ников в чате*/
.nick {
    color: #626466;
}
.cat_tooltip {
    background: #282828;
}

/*Летающий блок с параметрами*/

/*#parameter {
	position: fixed;
	width: 185px;
	top: 355px;
	left: 220px;
	border: none;
	border-radius: 10px;
	text-align: left;
	padding: 14px;
	background: none;
}*/

/*Обычный блок с параметрами*/
#parameter {
    border: none;
    text-align: left;
    padding: 10px;
    background: #0f0f0f;
}

/* Блок с местонахождением */
span#location {
    color: black;
    position: inherit;
    top: 10px;
    left: 80px;
    background-color: #3f404a;
    border: 0.5px solid black;
    border-radius: 10px;
    padding: 5px;
    font-weight: bold;
}
}

