    --brightgreen: rgb(0,255,0);
    --red: rgb(225,0,0);
    --darkgreen: rgb(0,40,0);
    --brightblue: rgb(0, 140, 222);
    --brightestblue: rgb(0, 200, 255);
    --white: rgb(255,255,255);
    --brightyellow: rgb(255,255,0);
    --black: rgb(0,0,0);
}


