/* ROUNDED EDGES */
.section-content, .rbx-tab, .profile-collections, .robux-table, .page-top, .people-list, .places-list, #groups-list, .section-content-off, .nav-tabs, .profile-avatar-mask, #groups-switcher, .dropdown-menu, .popover, #games-switcher, .switcher-items, #horizontal-tabs, .rbx-tab-heading, .recommended-items, .input-dropdown-btn, .item-card-thumb-container, #search-options, #MessagesInbox, .roblox-message-row, #GamesListsContainer, .games-list-container, .item-cards, #vertical-menu,.menu-secondary-option, .menu-secondary, .rbx-vip-server-item-container, #rbx-game-server-item-container, .rbx-game-server-item, .create-server-banner, .avatar-cards, #Notifications, .message-news, #groups-configure-vertical-menu, .audit-log, .rbx-friends-game-server-item, .badge-row, #rbx-gear-container, #MyTransactions_tab, #MyTransactions_TransactionTypeSelect, #TimePeriod, #TradeItems_TradeType, #Summary_tab, #TradeItems_tab, #SelectedGroupId, #LibraryTab, #DeveloperExchangeTab, #PayoutTab, .BuildPageContent, #build-dropdown-menu, .place-creationcontext-drop-down, .universe-selection-drop-down, #SortMain, #dropdownUl, .CatalogItemInner, #accept-fr-btn, #templates_tab, #basicsettings_tab, #access_tab, #advancedsettings_tab, #Genre, #MaxPlayersInput, #Access, #FriendSlotsInput, .back-button, #universe-configure, #navbar, .modal-content, #placeForm, .rbx-tab-content, .input-field, #AssetCreationContainer, .game-card-container, #game, #data, #payout, #configure-localization-tabs-navigation, .store-card, .item-card-container, .premium-card, .perks, .stack-list .stack-row, .trades-list-detail .col-xs-12, .tooltip, .conversation-title-input, .default-error-page {
