/* BG IMAGE */
#fb-root {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("https://i.imgur.com/Ku1vPFa.jpg") !important; /* osu! Fanart contest submission by Leisss */
    background-size: cover !important;
    background-attachment: fixed !important;
    filter: blur(4px) !important; 
}
}

@-moz-document regexp("http(s)?://(www|ref|forum|gnn|home|acg|guild|user|search|mailbox).*\.gamer\.com\.tw/(?!post|live).*"){
