@-moz-document domain("gradientmagic.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html
{
  background: #3d3d3f;
}

.gradient-box, .filter-bar,

.p-lg-4, .app-main-container
{
    background: #363639;
}

.bg-white,

.app-collection-list .cbox .title
{
    background: #363639 !important;
}

.btn,

.border-light,

.gradient-view #largePreview .bottom-bar
{
    background: #2e2e2f;
}

input
{
    background: #2e2e2f !important;
}

body, input, #app,

.btn,

.border-light *,

.app-collection-list .cbox .title,

.gradient-view #largePreview .bottom-bar *
{
  color: #ccc !important;
}

*,
.border-light
{
  border: none !important;
  box-shadow: none !important;
}

.gradient-view .bottom-bar .match-box
{
  border: 3px solid #525252 !important;
}

.mt-3
{
  margin-top: 0 !important;
}

#largePreview .close
{
  filter: invert(1);
}
}

