/* 
 * Fix for extended rating information userscript
 * on heritage rating modules
 */
  
    div.heritage-rating-module > div[style*="font-size: 8pt;"]{
        margin-top: 0px !important;
        margin-bottom: 0px !important;
    }
    
  ::-webkit-scrollbar-thumb {
    background: rgba(255, 0, 0, .3);
}

#page-title, body {
font-family: "Fira Sans", "DejaVu Sans Condensed", verdana, arial, helvetica, sans-serif !important;
  background: #191919 url("data:image/png;base64,") repeat !important;
}
    
select {
  background-color: #191919;
  color: #942828;
  border: 1px solid #808080;
  -moz-appearance: none;
  -webkit-appearance: none;
    background-image: url("data:image/png;base64,");
    background-repeat: no-repeat;
    background-position: 99% center;
    background-size: 13px;
    background-origin: padding-box;
}
    
input[type="button"], input[type="submit"] {
  border: 1px solid #808080;
  background-color: #191919;
  padding: 0px 8px 0px 8px;
  line-height: 19px;
  color: #942828;
}
    
input[type="text"] {
  background-color: #191919;
  color: #942828;
}
    
.pager .current{
  color: #191919;    
}

#page-title {
  font-family: "Junction", "Fira Sans", "DejaVu Sans Condensed", verdana, arial, helvetica, sans-serif !important;
  color: #942828;
}

a {
  color: #942828;
}

body, #license-area {
  color: #808080;
}

div#container-wrap {
  background: url(http://scp-wiki.wdfiles.com/local--files/roget-s-workbench/header.png) top left repeat-x;
}

/* ------------------------------------------------------------- */

#search-top-box-input, #search-top-box-input:hover, #search-top-box-input:focus {
  background-color: #191919;
  color: #808080;
}

/* ------------------------------------------------------------- */
/* ----------------------------TOP-BAR-------------------------- */
/* ------------------------------------------------------------- */

#top-bar a {
  color: #bbb;
  background: transparent;
}

#top-bar ul li.sfhover a, #top-bar ul li:hover a {
  background: #191919;
  color: #808080;
}

#top-bar ul li.sfhover ul li a, #top-bar ul li:hover ul li a {
  border-top: 1px solid #191919;
}

#top-bar ul li.sfhover a:hover, #top-bar ul li:hover a:hover {
  background: #191919;
}

#top-bar ul li ul a, #top-bar a:hover {
  color: #808080;
}

/* ------------------------------------------------------------- */
/* -----------------------RATING-WIDGET------------------------- */
/* ------------------------------------------------------------- */

.page-rate-widget-box {
  display: inline-block;
  margin-bottom: 10px;
  margin-right: 2em;
  color: #808080;
}

.page-rate-widget-box .rate-points {
  background-color: #191919 !important;
  border: solid 1px #808080;
  border-right: 0;
  border-radius: 5px 0 0 5px;
  color: #808080;
}

.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
  background-color: #808080;
  border-top: solid 1px #808080;
  border-bottom: solid 1px #808080;
  font-weight: bold;
  color: #191919;
}

.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
  background: transparent;
  color: #191919;
  padding: 0 4px;
  margin: 0 1px;
}

.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
  background: #191919;
  color: #808080;
}

.page-rate-widget-box .cancel {
  background-color: #191919;
  border: solid 1px #808080;
  border-left: 0;
  border-radius: 0 5px 5px 0;
  color: #808080;
}

.page-rate-widget-box .cancel a {
  background: transparent;
  color: #808080;
}

.page-rate-widget-box .cancel a:hover {
  border-radius: 0 3px 3px 0;
  background: #191919;
  color: #808080;
}

/* ------------------------------------------------------------- */

#side-bar .side-block {
padding: 10px;
border: 1px solid #808080;
border-radius: 10px;
margin-bottom: 15px;
background-color: #191919 !important;
color: #808080;
}
#side-bar .heading {
border-bottom: solid 1px #808080;
background-color: #191919;
color: #808080;
}
#side-bar .collapsible-block-unfolded-link {
border-bottom: solid 1px #808080;
background-color: #191919;
color: #808080;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
margin-top: 10px;
margin-bottom: 5px;
color: #808080;
background-color: #191919;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
background-color: #191919;
color: #808080;
}

/* ------------------------------------------------------------- */

.scp-image-block .scp-image-caption {
background-color: #191919;
color: #808080;
}

/* ------------------------------------------------------------- */

.yui-navset .yui-nav a {
background-color: #191919;
background-image: none;
color: #808080;
}

.yui-navset .yui-nav .selected a {
background: none;
background-image: none;
background-color: #191919;
color: #A0A0A0;
}

.yui-navset .yui-nav a:focus {
background: none;
background-image: none;
background-color: #191919;
color: #101010;
}

.yui-navset .yui-nav a:hover {
background: none;
background-image: none;
background-color: #808080;
color: #101010;
}

.yui-navset .yui-content {
background-color: #808080;
color: #191919;
}

/* ------------------------------------------------------------- */

blockquote {
background-color: #808080;
color: #191919;
border: 1px dashed #191919;
}

/* ------------------------------------------------------------- */
.content-panel {
background-color: #191919 !important;
color: #808080 !important;
box-shadow: none;
}

.content-toc {
background-color: #808080 !important;
color: #191919 !important;
border: none !important;
}

/* ------------------------------------------------------------- */

.footnote, .hovertip {
background: #191919 !important;
}

.hovertip {
  border: 1px solid #808080 !important;
}
/* ------------------------------------------------------------- */

.post .head, .description-block, .description-block .head, .description-block .statistics {
  background: none !important;
  background-color: #808080 !important;
  color: #191919 !important;
}
    
.head .options a {
  color: #942020 !important;
}
    
.panel-heading, .panel-footer{
  color: #808080 !important;
}
    
.panel-heading, .panel-footer a{
  color: #d0d0d0 !important;
}
    
.content-type-title, .content-type-description-2 table tbody tr:first-child{
  color: #808080 !important;
  border-radius: 2px !important;
  box-shadow: none !important;
  background: none !important;
}

/* slight fix because the links are too close to be easily readable */
.forum-start-box .last > a{
  margin-left: 4px;
}
    
.forum-start-box .head{
  background-color: #191919 !important;
