@-moz-document url-prefix("http://scpfoundation.net/"), url-prefix("http://interwiki.scpdb.org"), url-prefix("http://scp-ru.wdfiles.com/") {
#page-title {
    color: #fff;
    font-weight: bold;
    border-color: #333333;
}
li, p {
    line-height: 150%;
}
a {
    color: #737373;
}
#header, #top-bar {
    width: auto;
    margin: 0 10px;
}
body {
    color: #a6a6a6;
}

#search-top-box-input {
    border-radius: 0;
    border: none;
    padding: 10px;
    box-shadow: none;
    background-color: #1a1a1a;
    width: 160px;
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    box-shadow: none;
    border: none;
    background-color: #262626;
}
#search-top-box-form input[type=submit] {
    background: #00000080;
    box-shadow: none;
    border-radius: 0;
    border: none;
    padding: 10px;
    font-weight: lighter;
    font-size: 13px;
    background-color: #333333;
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    text-shadow: 0 0 1px #ffffff40;
    border: none;
    box-shadow: none;
    background-color: #4d4d4d;
}

#login-status {
    text-shadow: none;
}

#header h2 span {
    text-shadow: none;
    font-weight: lighter;
    color: #fff;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #4d4d4d;
}

#top-bar ul > li > ul {
    border: none;
    border-radius: 0;
    box-shadow: none;
    background-color: #141414;
}

#side-bar .side-block {
    border: none;
    border-radius: 0px;
    box-shadow: 0 0px 0px 0px #cccccc;
    background-color: #141414 !important;
}

#side-bar div.menu-item img {
    filter: saturate(0%);
}

#side-bar .side-area {
    color: #4d4d4d;
    border-bottom: none;
    font-weight: lighter;
    border: none;
    padding-left: 0;
}

#side-bar .heading {
    color: #4d4d4d;
    border-bottom: none;
    border: solid 1px #666;
    font-weight: lighter;
    border: none;
    padding-left: 0;
